export enum CJoliActions {
  LOAD_TOURNEYS = "LOAD_TOURNEYS",
  SELECT_TOURNEY = "SELECT_TOURNEY",
  LOAD_RANKING = "LOAD_RANKING",
  SELECT_DAY = "SELECT_DAY",
  LOAD_TOURNEY = "LOAD_TOURNEY",
  SET_COLOR = "SET_COLOR",
  SELECT_PAGE = "SELECT_PAGE",
}

export enum UserActions {
  LOAD_USER = "LOAD_USER",
  COUNT_USER = "COUNT_USER",
}

import React, { Dispatch } from "react";
import {
  Match,
  Phase,
  Position,
  Ranking,
  Squad,
  Team,
  Tourney,
  TypePage,
} from "../models";
import { CJoliActions } from "./actions";
import dayjs from "dayjs";

interface CJoliState {
  tourneys?: Tourney[];
  tourney?: Tourney;
  ranking?: Ranking;
  teams?: Team[];
  phases?: Phase[];
  squads?: Squad[];
  positions?: Position[];
  matches: Match[];
  daySelected: string;
  theme: {
    primary: string;
    secondary: string;
  };
  page: TypePage;
}

export const CJoliContext = React.createContext<{
  state: CJoliState;
  dispatch: Dispatch<Action>;
} | null>(null);

const initialState: CJoliState = {
  daySelected: dayjs().format("YYYY-MM-DD"),
  matches: [],
  theme: {
    primary: "#202644",
    secondary: "#932829",
  },
  page: "welcome",
};

type Action =
  | {
      type: CJoliActions.LOAD_TOURNEYS;
      payload: Tourney[];
    }
  | {
      type: CJoliActions.SELECT_TOURNEY;
      payload: Tourney;
    }
  | {
      type: CJoliActions.LOAD_RANKING;
      payload: Ranking;
    }
  | { type: CJoliActions.SELECT_DAY; payload: string }
  | { type: CJoliActions.LOAD_TOURNEY; payload: Tourney }
  | {
      type: CJoliActions.SET_COLOR;
      payload: { primary: string; secondary: string };
    }
  | {
      type: CJoliActions.SELECT_PAGE;
      payload: TypePage;
    };

const reducer = (state: CJoliState, action: Action) => {
  switch (action.type) {
    case CJoliActions.LOAD_TOURNEYS: {
      return { ...state, tourneys: action.payload };
    }
    case CJoliActions.LOAD_TOURNEY: {
      const tourney = action.payload;
      const teams = tourney.teams;
      teams.sort((a, b) => (a.name > b.name ? 1 : -1));
      return { ...state, tourney: tourney };
    }
    case CJoliActions.SELECT_TOURNEY: {
      return {
        ...state,
        tourney: action.payload,
      };
    }
    case CJoliActions.LOAD_RANKING: {
      const ranking = action.payload;
      const tourney = ranking.tourney;
      const teams = tourney.teams;
      teams.sort((a, b) => (a.name > b.name ? 1 : -1));
      const phases = tourney.phases;
      const squads = phases.reduce<Squad[]>(
        (acc, phase) => [...acc, ...phase.squads],
        []
      );
      const positions = squads.reduce<Position[]>(
        (acc, squad) => [...acc, ...squad.positions],
        []
      );
      const matches = squads.reduce<Match[]>(
        (acc, squad) => [...acc, ...squad.matches],
        []
      );
      return {
        ...state,
        ranking,
        tourney,
        teams,
        phases,
        squads,
        positions,
        matches,
      };
    }
    case CJoliActions.SELECT_DAY: {
      return {
        ...state,
        daySelected: action.payload,
      };
    }
    case CJoliActions.SET_COLOR: {
      return { ...state, theme: action.payload };
    }
    case CJoliActions.SELECT_PAGE: {
      return { ...state, page: action.payload };
    }
  }
};

export const CJoliProvider = ({ children }: { children: React.ReactNode }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  return (
    <CJoliContext.Provider
      value={{
        state,
        dispatch,
      }}
    >
      {children}
    </CJoliContext.Provider>
  );
};

import React, { Dispatch } from "react";
import { User } from "../models";
import { UserActions } from "./actions";

interface UserState {
  user?: User;
  countUser: number;
}

export const UserContext = React.createContext<{
  state: UserState;
  dispatch: Dispatch<Action>;
} | null>(null);

const initialState: UserState = { countUser: 0 };

type Action =
  | {
      type: UserActions.LOAD_USER;
      payload?: User;
    }
  | { type: UserActions.COUNT_USER; payload: number };

const reducer = (state: UserState, action: Action) => {
  switch (action.type) {
    case UserActions.LOAD_USER: {
      const user = action.payload;
      return { ...state, user };
    }
    case UserActions.COUNT_USER:
      return { ...state, countUser: action.payload };
  }
  return state;
};

export const UserProvider = ({ children }: { children: React.ReactNode }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  return (
    <UserContext.Provider
      value={{
        state,
        dispatch,
      }}
    >
      {children}
    </UserContext.Provider>
  );
};

import React, { ReactNode } from "react";

type Type = "danger" | "success";

interface ToastState {
  show: boolean;
  type: Type;
  message: string;
}

export const ToastContext = React.createContext<{
  state: ToastState;
  hideToast: () => void;
  showToast: (type: Type, message: string) => void;
} | null>(null);

export const ToastProvider = ({ children }: { children: ReactNode }) => {
  const [state, setState] = React.useState<ToastState>({
    show: false,
    type: "success",
    message: "",
  });
  const showToast = async (type: Type, message: string) => {
    if (state.show) {
      hideToast();
      await new Promise((resolve) => setTimeout(resolve, 300));
    }
    setState({ show: true, type, message });
  };
  const hideToast = () => {
    setState({ ...state, show: false });
  };
  return (
    <ToastContext.Provider value={{ state, showToast, hideToast }}>
      {children}
    </ToastContext.Provider>
  );
};

import { createContext, ReactNode, useState } from "react";

export const ModalContext = createContext<{
  show: Record<string, boolean>;
  setShow: (show: Record<string, boolean>) => void;
  data: Record<string, object>;
  setData: (data: Record<string, object>) => void;
} | null>(null);

export const ModalProvider = ({ children }: { children: ReactNode }) => {
  const [show, setShow] = useState<Record<string, boolean>>({});
  const [data, setData] = useState<Record<string, object>>({});
  return (
    <ModalContext.Provider value={{ show, setShow, data, setData }}>
      {children}
    </ModalContext.Provider>
  );
};

import styled from "@emotion/styled";
import { ReactNode } from "react";
import { Container } from "react-bootstrap";

const MyProgressBar = styled("div")`
  height: 4px;
  width: 100%;
  --c: no-repeat linear-gradient(#6100ee 0 0);
  background: var(--c), var(--c), #d7b8fc;
  background-size: 60% 100%;
  animation: l16 3s infinite;
  @keyframes l16 {
    0% {
      background-position: -150% 0, -150% 0;
    }
    66% {
      background-position: 250% 0, -150% 0;
    }
    100% {
      background-position: 250% 0, 250% 0;
    }
  }
`;

const Waiting = ({
  ready,
  children,
}: {
  ready: boolean;
  children: ReactNode;
}) => {
  return (
    <>
      {!ready && (
        <Container className="p-4" fluid>
          <MyProgressBar />
        </Container>
      )}
      {ready && children}
    </>
  );
};

export default Waiting;

import React from "react";
import { ModalContext } from "../contexts/ModalContext";

export const useModal = <T,>(id: string) => {
  const ctx = React.useContext(ModalContext);
  if (!ctx) {
    throw new Error("useModal has to be used within <ModalProvider>");
  }
  const setShow = (show: boolean) => ctx.setShow({ ...ctx.show, [id]: show });
  const setShowWithData = (show: boolean, data: T) => {
    ctx.setData({ ...ctx.data, [id]: data as object });
    setShow(show);
  };
  return {
    show: ctx.show[id],
    data: (ctx.data[id] || undefined) as T | undefined,
    setShow,
    setShowWithData,
  };
};

export const useModals = () => {
  const ctx = React.useContext(ModalContext);
  if (!ctx) {
    throw new Error("useModal has to be used within <ModalProvider>");
  }
  return {
    setShow: (id: string, show: boolean) =>
      ctx.setShow({ ...ctx.show, [id]: show }),
  };
};

import {
  Fade,
  Modal,
  Form,
  Button,
  ProgressBar,
  Spinner,
} from "react-bootstrap";
import {
  useForm,
  SubmitHandler,
  FieldValues,
  Path,
  PathValue,
} from "react-hook-form";
import { useEffect, useState } from "react";
import { useModal } from "../hooks/useModal";
import { Trans, useTranslation } from "react-i18next";
import CreatableSelect from "react-select/creatable";
import Select, { SingleValue } from "react-select";

export interface Field<T extends FieldValues> {
  id: Path<T>;
  label: string;
  type:
    | "text"
    | "password"
    | "date"
    | "datetime-local"
    | "number"
    | "select"
    | "switch";
  required?: boolean;
  validate?: Path<T>;
  autoFocus?: boolean;
  creatable?: boolean;
  options?: { label: string; value: string | number }[];
  onChange?: (value?: string) => void;
  testId?: string;
}

interface CJoliModalProps<T extends FieldValues> {
  id: string;
  title: string;
  fields: Field<T>[];
  onSubmit: (data: T) => Promise<boolean>;
  values?: Partial<T>;
}

const CJoliModal = <T extends FieldValues>({
  id,
  title,
  fields,
  onSubmit,
  values,
}: CJoliModalProps<T>) => {
  const { show, setShow } = useModal(id);
  const { t } = useTranslation();

  const {
    register,
    setValue,
    handleSubmit,
    formState: { errors },
    resetField,
    watch,
    setFocus,
  } = useForm<T>({ values: values as T });

  const [running, setRunning] = useState(false);

  const focusField = fields.find((f) => f.autoFocus);
  useEffect(() => {
    focusField && show && setFocus(focusField.id);
  }, [focusField, show, setFocus]);

  const submit: SubmitHandler<T> = async (data) => {
    setRunning(true);
    const result = await onSubmit(data);
    if (result) {
      setShow(false);
    }
    setRunning(false);
  };

  const createInput = (f: Field<T>) => {
    switch (f.type) {
      case "select": {
        const onChange = (
          v: SingleValue<{ label: string; value: string | number }>
        ) => {
          const value = v?.value as PathValue<T, Path<T>>;
          setValue(f.id, value);
          f.onChange && f.onChange(value);
        };
        return f.creatable ? (
          <CreatableSelect
            options={f.options}
            onChange={onChange}
            isClearable
            defaultValue={f.options?.find(
              (o) => values && o.value == values[f.id]
            )}
            aria-label={f.label}
          />
        ) : (
          <Select
            options={f.options}
            onChange={onChange}
            isClearable
            defaultValue={f.options?.find(
              (o) => values && o.value == values[f.id]
            )}
            aria-label={f.label}
          />
        );
      }
      case "switch":
        return (
          <Form.Check
            type="switch"
            label={f.label}
            {...register(f.id, {
              required: f.required
                ? t("error.required", { field: f.label })
                : false,
              validate: (value) =>
                f.validate && watch(f.validate) !== value
                  ? t("error.invalid", { field: f.label })
                  : undefined,
            })}
            className={`${errors[f.id] ? "is-invalid" : ""}`}
          />
        );
      default:
        return (
          <Form.Control
            type={f.type}
            data-testid={f.testId}
            autoFocus={f.autoFocus}
            {...register(f.id, {
              required: f.required
                ? t("error.required", { field: f.label })
                : false,
              validate: (value) =>
                f.validate && watch(f.validate) !== value
                  ? t("error.invalid", { field: f.label })
                  : undefined,
            })}
            className={`${errors[f.id] ? "is-invalid" : ""}`}
          />
        );
    }
  };

  return (
    <Modal
      show={show}
      onHide={() => setShow(false)}
      backdrop="static"
      keyboard={false}
      centered
      style={{ color: "black" }}
      onShow={() => {
        fields.map((f) => resetField(f.id));
        setRunning(false);
      }}
    >
      <Form onSubmit={handleSubmit(submit)}>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {fields.map((f) => (
            <Form.Group key={f.id} className="mb-3" controlId={f.id as string}>
              <Form.Label>{f.label}</Form.Label>
              {createInput(f)}
              <Form.Control.Feedback type="invalid">
                {errors[f.id]?.message as string}
              </Form.Control.Feedback>
            </Form.Group>
          ))}
          <Fade in={running}>
            <ProgressBar now={100} animated />
          </Fade>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShow(false)}>
            <Trans i18nKey="button.cancel">Cancel</Trans>
          </Button>
          <Button type="submit" variant="primary" disabled={running}>
            <Trans i18nKey="button.submit">Submit</Trans>
            {running && (
              <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
                className="mx-2"
              />
            )}
          </Button>
        </Modal.Footer>
      </Form>
    </Modal>
  );
};

export default CJoliModal;

import { useCallback, useContext, useEffect } from "react";
import { CJoliContext } from "../contexts/CJoliContext";
import {
  Match,
  Phase,
  Rank,
  Ranking,
  Squad,
  Team,
  Tourney,
  TypePage,
} from "../models";
import { CJoliActions } from "../contexts/actions";

export const useCJoli = (page?: TypePage) => {
  const ctx = useContext(CJoliContext);
  if (!ctx) {
    throw new Error("useCJoli has to be used within <CJoliProvider>");
  }

  const { state, dispatch } = ctx;
  const loadTourneys = useCallback(
    (tourneys: Tourney[]) =>
      dispatch({ type: CJoliActions.LOAD_TOURNEYS, payload: tourneys }),
    [dispatch]
  );
  const selectTourney = useCallback(
    (tourney: Tourney) =>
      dispatch({ type: CJoliActions.SELECT_TOURNEY, payload: tourney }),
    [dispatch]
  );

  const loadRanking = useCallback(
    (ranking: Ranking) =>
      dispatch({ type: CJoliActions.LOAD_RANKING, payload: ranking }),
    [dispatch]
  );

  const loadTourney = useCallback(
    (tourney: Tourney) =>
      dispatch({ type: CJoliActions.LOAD_TOURNEY, payload: tourney }),
    [dispatch]
  );

  const getSquad = useCallback(
    (squadId: number) => {
      const squad = state.squads?.find((s) => s.id === squadId);
      return squad;
    },
    [state]
  );
  const getTeam = useCallback(
    (teamId: number) => state.teams?.find((t) => t.id === teamId),
    [state.teams]
  );
  const getPosition = useCallback(
    (positionId: number) => state.positions?.find((p) => p.id === positionId),
    [state.positions]
  );
  const findTeam = useCallback(
    ({ positionId }: { positionId?: number }) => {
      if (positionId) {
        const position = getPosition(positionId);
        return position && getTeam(position.teamId);
      }
      return undefined;
    },
    [getPosition, getTeam]
  );
  const getTeamInfo = useCallback(
    (positionId: number, defaultName?: string) => {
      const position = getPosition(positionId);
      if (!position) return { name: defaultName };
      const team = getTeam(position.teamId);
      if (!team) {
        return { name: (defaultName ?? position.name) || "noname" };
      }
      const name = position.name
        ? `${team?.name} - ${position.short}`
        : team?.name || "noname";
      return { name, logo: team?.logo };
    },
    [getPosition, getTeam]
  );

  const getScoreFromPosition = useCallback(
    (positionId: number, squadId: number) => {
      const scoreSquad = state.ranking?.scores.scoreSquads.find(
        (s) => s.squadId == squadId
      );
      return scoreSquad?.scores.find((s) => s.positionId == positionId);
    },
    [state.ranking]
  );

  const getRankPosition = useCallback(
    (rank: Rank) => {
      const scoreSquad = state.ranking?.scores.scoreSquads.find(
        (s) => s.squadId == rank.squadId
      );
      const score = scoreSquad?.scores[rank.value - 1];
      return score?.positionId;
    },
    [state.ranking]
  );
  const getTeamRank = useCallback(
    (team: Team) => {
      for (const rank of state.tourney?.ranks || []) {
        const positionId = getRankPosition(rank);
        if (!positionId) {
          continue;
        }
        const position = getPosition(positionId);
        if (position?.teamId == team.id) {
          return rank;
        }
      }
      return undefined;
    },
    [getPosition, getRankPosition, state.tourney?.ranks]
  );

  const isTeamInPhase = useCallback((teamId: number, phase: Phase) => {
    return phase.squads.some((squad) =>
      squad.positions.some((position) => position.teamId == teamId)
    );
  }, []);
  const isTeamInSquad = useCallback((teamId: number, squad: Squad) => {
    return squad.positions.some((position) => position.teamId == teamId);
  }, []);
  const isTeamInMatch = useCallback(
    (teamId: number, match: Match) => {
      return (
        getPosition(match.positionIdA)?.teamId == teamId ||
        getPosition(match.positionIdB)?.teamId == teamId
      );
    },
    [getPosition]
  );

  const getScoreForTeam = useCallback(
    (team: Team) => {
      return state.ranking?.scores.scoreTeams[team.id];
    },
    [state.ranking?.scores.scoreTeams]
  );

  const getScoreFromSquad = useCallback(
    (squad: Squad, team: Team) => {
      const scoreSquad = state.ranking?.scores.scoreSquads.find(
        (s) => s.squadId == squad.id
      );
      return scoreSquad?.scores.find((s) => s.teamId == team.id);
    },
    [state]
  );

  const selectDay = useCallback(
    (day: string) => dispatch({ type: CJoliActions.SELECT_DAY, payload: day }),
    [dispatch]
  );

  const setColor = useCallback(
    (primary: string, secondary: string) =>
      dispatch({
        type: CJoliActions.SET_COLOR,
        payload: { primary, secondary },
      }),
    [dispatch]
  );

  const selectPage = useCallback(
    (page: TypePage) =>
      dispatch({ type: CJoliActions.SELECT_PAGE, payload: page }),
    [dispatch]
  );

  useEffect(() => {
    page && selectPage(page);
  }, [page, selectPage]);

  return {
    ...state,
    loadRanking,
    loadTourneys,
    loadTourney,
    selectTourney,
    getSquad,
    getTeam,
    findTeam,
    getPosition,
    getTeamInfo,
    getRankPosition,
    getTeamRank,
    isTeamInPhase,
    isTeamInSquad,
    isTeamInMatch,
    getScoreForTeam,
    selectDay,
    getScoreFromPosition,
    getScoreFromSquad,
    setColor,
    selectPage,
    isHomePage: state.page == "home",
  };
};

import React from "react";
import { ToastContext } from "../contexts/ToastContext";

export const useToast = () => {
  const ctx = React.useContext(ToastContext);
  if (!ctx) {
    throw new Error("useToast has to be used within <ToastProvider>");
  }
  return ctx;
};

import { useParams } from "react-router-dom";

const useUid = () => {
  const { uid } = useParams();

  const host = window.location.host;
  const uidDomain = host.split(".")[0];
  if (
    uidDomain &&
    uidDomain != "www" &&
    uidDomain != "cjoli-hockey" &&
    !uidDomain.startsWith("localhost")
  ) {
    return uidDomain;
  }

  return uid!;
};

export default useUid;

import { useCallback, useContext } from "react";
import { UserContext } from "../contexts/UserContext";
import { User, UserConfig } from "../models";
import { UserActions } from "../contexts/actions";
import { useCJoli } from "./useCJoli";
import * as cjoliService from "../services/cjoliService";
import useUid from "./useUid";

export const useUser = () => {
  const ctx = useContext(UserContext);
  const { loadRanking } = useCJoli();
  const uid = useUid();
  if (!ctx) {
    throw new Error("useUser has to be used within <UserProvider>");
  }

  const { state, dispatch } = ctx;
  const loadUser = useCallback(
    (user?: User) => dispatch({ type: UserActions.LOAD_USER, payload: user }),
    [dispatch]
  );
  const setCountUser = useCallback(
    (count: number) =>
      dispatch({ type: UserActions.COUNT_USER, payload: count }),
    [dispatch]
  );

  const handleSaveUserConfig = useCallback(
    async (userConfig: UserConfig) => {
      try {
        const ranking = await cjoliService.saveUserConfig(uid, userConfig);
        loadRanking(ranking);
        const configs =
          state.user?.configs?.map((c) =>
            c.tourneyId == userConfig.tourneyId ? userConfig : c
          ) || [];
        loadUser({ ...state.user, configs } as User);
      } catch (error) {
        console.error("Unable to save userConfig", error);
      }
    },
    [loadRanking, loadUser, uid, state.user]
  );

  const { tourney } = useCJoli();

  return {
    ...state,
    isConnected: state.user,
    isAdmin: state.user?.role === "ADMIN",
    userConfig: state.user?.configs?.find(
      (c) => c.tourneyId == tourney?.id
    ) || { tourneyId: 0, useCustomEstimate: false, favoriteTeamId: 0 },
    loadUser,
    setCountUser,
    handleSaveUserConfig,
  };
};

import { useTranslation } from "react-i18next";
import CJoliModal, { Field } from "../components/CJoliModal";
import { useCJoli } from "../hooks/useCJoli";
import { useToast } from "../hooks/useToast";
import useUid from "../hooks/useUid";
import { useUser } from "../hooks/useUser";
import { User } from "../models";
import * as cjoliService from "../services/cjoliService";
import { memo } from "react";

const LoginModal = () => {
  const { loadRanking } = useCJoli();
  const { loadUser } = useUser();
  const { showToast } = useToast();
  const uid = useUid();
  const { t } = useTranslation();

  const fields: Field<User>[] = [
    {
      id: "login",
      label: t("login.form.login", "Login"),
      type: "text",
      required: true,
      autoFocus: true,
    },
    {
      id: "password",
      label: t("login.form.password", "Password"),
      type: "password",
      required: true,
    },
  ];
  const onSubmit = async (user: User) => {
    const result = await cjoliService.login(user);
    if (!result) {
      showToast("danger", t("login.error.invalidLogin", "Invalid login"));
      return false;
    } else {
      user = await cjoliService.getUser();
      loadUser(user);
      if (uid) {
        const ranking = await cjoliService.getRanking(uid);
        loadRanking(ranking);
      }
      return true;
    }
  };
  return (
    <CJoliModal<User>
      id="login"
      title={t("login.title.login", "Login")}
      fields={fields}
      onSubmit={onSubmit}
    />
  );
};

export const LoginModalMemo = memo(LoginModal);

export default LoginModalMemo;

import { useTranslation } from "react-i18next";
import CJoliModal, { Field } from "../components/CJoliModal";
import { useToast } from "../hooks/useToast";
import { useUser } from "../hooks/useUser";
import { User } from "../models";
import * as cjoliService from "../services/cjoliService";

type UserRegister = User & { passwordConfirm: string };

const RegisterModal = () => {
  const { loadUser } = useUser();
  const { showToast } = useToast();
  const { t } = useTranslation();

  const fields: Field<UserRegister>[] = [
    {
      id: "login",
      label: t("login.form.login", "Login"),
      type: "text",
      required: true,
      autoFocus: true,
    },
    {
      id: "password",
      label: t("login.form.password", "Password"),
      type: "password",
      required: true,
    },
    {
      id: "passwordConfirm",
      label: t("login.form.confirmPassword", "Confirm Password"),
      type: "password",
      required: true,
      validate: "password",
    },
  ];
  const onSubmit = async (user: UserRegister) => {
    const result = await cjoliService.register(user);
    if (!result) {
      showToast(
        "danger",
        t("login.error.register", "Unable to register account")
      );
      return false;
    } else {
      const user = await cjoliService.getUser();
      loadUser(user);
      showToast("success", t("login.success.register", "Account created"));
      return true;
    }
  };
  return (
    <CJoliModal
      id="register"
      title={t("login.title.register", "Register")}
      fields={fields}
      onSubmit={onSubmit}
    />
  );
};

export default RegisterModal;

