import {
  DefaultError,
  queryOptions,
  UseMutationOptions,
} from "@tanstack/react-query";
import { useCallback } from "react";
import * as cjoliService from "../services/cjoliService";
import * as settingService from "../services/settingService";
import { useUser } from "./useUser";
import { useCJoli } from "./useCJoli";
import { Match, Phase, Position, Rank, Squad, Team, Tourney } from "../models";
import { useNavigate } from "react-router-dom";

export const mutationOptions = <
  TData = unknown,
  TError = DefaultError,
  TVariables = void,
  TContext = unknown,
>(
  options: UseMutationOptions<TData, TError, TVariables, TContext>
): UseMutationOptions<TData, TError, TVariables, TContext> => {
  return options;
};

const useApiGet = () => {
  const { loadTourneys, loadRanking } = useCJoli();
  const { loadUser } = useUser();

  const getUser = useCallback(
    () =>
      queryOptions({
        queryKey: ["getUser"],
        queryFn: async () => {
          const user = await cjoliService.getUser();
          loadUser(user);
          return user;
        },
      }),
    [loadUser]
  );

  const getTourneys = useCallback(
    ({ enabled }: { enabled?: boolean }) =>
      queryOptions({
        queryKey: ["getTourneys"],
        queryFn: async () => {
          const tourneys = await cjoliService.getTourneys();
          loadTourneys(tourneys);
          return tourneys;
        },
        enabled,
      }),
    [loadTourneys]
  );

  const getTeams = useCallback(
    () =>
      queryOptions({
        queryKey: ["getTeams"],
        queryFn: async () => settingService.getTeams(),
        initialData: [],
      }),
    []
  );

  const getRanking = useCallback(
    (uid: string) =>
      queryOptions({
        queryKey: ["getRanking", uid],
        queryFn: async () => {
          const ranking = await cjoliService.getRanking(uid);
          loadRanking(ranking);
          return ranking;
        },
      }),
    [loadRanking]
  );
  return { getUser, getTourneys, getTeams, getRanking };
};

const useApiPost = () => {
  const { loadTourney } = useCJoli();

  const saveTourney = useCallback(
    ({ onSuccess }: { onSuccess?: () => void }) =>
      mutationOptions({
        mutationKey: ["addTourney"],
        mutationFn: async (tourney: Tourney) => {
          const t = await settingService.importTourney(tourney);
          loadTourney(t);
          return t;
        },
        onSuccess,
      }),
    [loadTourney]
  );
  return { saveTourney };
};

const useApiDelete = () => {
  const { loadTourney } = useCJoli();
  const navigate = useNavigate();

  const removeTourney = useCallback(
    (uid: string) =>
      mutationOptions({
        mutationKey: ["removeTourney"],
        mutationFn: async () => {
          await settingService.removeTourney({ uid });
          navigate("/");
          return true;
        },
      }),
    [navigate]
  );

  const removeTeam = useCallback(
    (uid: string) =>
      mutationOptions({
        mutationKey: ["removeTeam"],
        mutationFn: async (team: Team) => {
          const tourney = await settingService.removeTeam({
            uid,
            teamId: team.id,
          });
          loadTourney(tourney);
          return true;
        },
      }),
    [loadTourney]
  );

  const removePhase = useCallback(
    (uid: string) =>
      mutationOptions({
        mutationKey: ["removePhase"],
        mutationFn: async (phase: Phase) => {
          const tourney = await settingService.removePhase({
            uid,
            phaseId: phase.id,
          });
          loadTourney(tourney);
          return true;
        },
      }),
    [loadTourney]
  );

  const removeSquad = useCallback(
    (uid: string) =>
      mutationOptions({
        mutationKey: ["removeSquad", uid],
        mutationFn: async ({
          squad,
          phase,
        }: {
          squad: Squad;
          phase: Phase;
        }) => {
          const tourney = await settingService.removeSquad({
            uid,
            phaseId: phase.id,
            squadId: squad.id,
          });
          loadTourney(tourney);
          return true;
        },
      }),
    [loadTourney]
  );

  const removePosition = useCallback(
    (uid: string) =>
      mutationOptions({
        mutationKey: ["removePosition"],
        mutationFn: async ({
          position,
          phase,
          squad,
        }: {
          position: Position;
          phase: Phase;
          squad: Squad;
        }) => {
          const tourney = await settingService.removePosition({
            uid,
            phaseId: phase.id,
            squadId: squad.id,
            positionId: position.id,
          });
          loadTourney(tourney);
          return true;
        },
      }),
    [loadTourney]
  );

  const removeMatch = useCallback(
    (uid: string) =>
      mutationOptions({
        mutationKey: ["removeMatch"],
        mutationFn: async ({
          match,
          squad,
          phase,
        }: {
          match: Match;
          squad: Squad;
          phase: Phase;
        }) => {
          const tourney = await settingService.removeMatch({
            uid,
            phaseId: phase.id,
            squadId: squad.id,
            matchId: match.id,
          });
          loadTourney(tourney);
          return true;
        },
      }),
    [loadTourney]
  );

  const removeRank = useCallback(
    (uid: string) =>
      mutationOptions({
        mutationKey: ["removeRank"],
        mutationFn: async (rank: Rank) => {
          const tourney = await settingService.removeRank({
            uid,
            rankId: rank.id,
          });
          loadTourney(tourney);
          return true;
        },
      }),
    [loadTourney]
  );

  return {
    removeTourney,
    removeTeam,
    removePhase,
    removeSquad,
    removePosition,
    removeMatch,
    removeRank,
  };
};

export const useApi = () => {
  const get = useApiGet();
  const post = useApiPost();
  const del = useApiDelete();

  return {
    ...get,
    ...post,
    ...del,
  };
};

import { ReactNode, useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import { scroller, scrollSpy } from "react-scroll";

interface ScrollButtonProps {
  to: string;
  icon: ReactNode;
  down: boolean;
}
const ScrollButton = ({ to, icon, down }: ScrollButtonProps) => {
  const [show, setShow] = useState(true);
  useEffect(() => {
    scrollSpy.mount(document);
    scrollSpy.addSpyHandler((_x: number, y: number) => {
      const c = scroller.get(to);
      const maxy = Math.round(
        c ? c.getBoundingClientRect().top + window.scrollY : 0
      );
      setShow(down ? y <= maxy : y > maxy);
    }, document);

    return () => {
      scrollSpy.unmount(null, null);
    };
  }, [to, down]);
  return (
    <>
      {show && (
        <>
          Bonjour
          <Button
            onClick={() => scroller.scrollTo(to, {})}
            role="button"
            data-testid="scroll"
          >
            {icon}
          </Button>
        </>
      )}
    </>
  );
};

export default ScrollButton;

export const useColor = () => {
  const checkRange = (count: number, start: number, end: number) => {
    if (count < start) return start;
    if (count > end) return end;
    return count;
  };

  const getFullHEX = (hex: string) => {
    const [r, g, b] = hex.slice(1, 4).split("");
    return `#${r}${r}${g}${g}${b}${b}`;
  };

  const checkColor = (color: string) => {
    if (typeof color !== "string") return { hex: color, check: false };
    color = color.toLowerCase();
    const isHEX = color.slice(0, 1) === "#";

    if (isHEX) {
      const validHEX = color.search(/^#[a-f0-9]{3}([a-f0-9]{3})?$/) !== -1;
      return {
        hex: validHEX && color.length === 4 ? getFullHEX(color) : color,
        check: validHEX,
      };
    }
    return { hex: color, check: false };
  };

  const checkRGB = (r: number, g: number, b: number) => {
    r = checkRange(r, 0, 255);
    g = checkRange(g, 0, 255);
    b = checkRange(b, 0, 255);
    return { r, g, b };
  };

  const HEXToRGB = (hex: string) => ({
    r: parseInt(hex.slice(1, 3), 16),
    g: parseInt(hex.slice(3, 5), 16),
    b: parseInt(hex.slice(5, 7), 16),
  });

  const calculH = (
    delta: number,
    max: number,
    { r, g, b }: { r: number; g: number; b: number }
  ) => {
    let h = 0;
    if (delta === 0) {
      h = 0;
    } else if (max === r) {
      h = ((g - b) / delta) % 6;
    } else if (max === g) {
      h = (b - r) / delta + 2;
    } else if (max === b) {
      h = (r - g) / delta + 4;
    }

    h = Math.round(h * 60);
    h = h < 0 ? h + 360 : h;
    return h;
  };

  const RGBToHSL = (r: number, g: number, b: number) => {
    r /= 255;
    g /= 255;
    b /= 255;

    const min = Math.min(r, g, b);
    const max = Math.max(r, g, b);
    const delta = max - min;
    let [h, s, l] = [0, 0, 0];

    h = calculH(delta, max, { r, g, b });

    l = (max + min) / 2;
    s = delta === 0 ? 0 : delta / (1 - Math.abs(2 * l - 1));

    l = +(l * 100).toFixed(1);
    s = +(s * 100).toFixed(1);

    l = checkRange(l, 0, 100);
    s = checkRange(s, 0, 100);

    return { h, s, l };
  };

  // eslint-disable-next-line max-statements
  const calculRgb = ({ h, c, x }: { h: number; c: number; x: number }) => {
    let [r, g, b] = [0, 0, 0];

    if (0 <= h && h < 60) {
      r = c;
      g = x;
      b = 0;
    } else if (60 <= h && h < 120) {
      r = x;
      g = c;
      b = 0;
    } else if (120 <= h && h < 180) {
      r = 0;
      g = c;
      b = x;
    } else if (180 <= h && h < 240) {
      r = 0;
      g = x;
      b = c;
    } else if (240 <= h && h < 300) {
      r = x;
      g = 0;
      b = c;
    } else if (300 <= h && h < 360) {
      r = c;
      g = 0;
      b = x;
    }
    return { r, g, b };
  };

  const HSLToRGB = (h: number, s: number, l: number) => {
    s /= 100;
    l /= 100;

    const c = (1 - Math.abs(2 * l - 1)) * s;
    const x = c * (1 - Math.abs(((h / 60) % 2) - 1));
    const m = l - c / 2;
    let { r, g, b } = calculRgb({ c, h, x });

    r = Math.round((r + m) * 255);
    g = Math.round((g + m) * 255);
    b = Math.round((b + m) * 255);

    return checkRGB(r, g, b);
  };

  const getHEX = (code: number) => {
    const hex = code.toString(16);
    return hex.length === 1 ? `0${hex}` : hex;
  };

  const RGBToHEX = (r: number, g: number, b: number) =>
    `#${getHEX(r)}${getHEX(g)}${getHEX(b)}`;

  const lightness = (color: string, percent = 0) => {
    const { hex, check } = checkColor(color);
    if (!check || typeof percent !== "number" || percent === 0) return color;
    percent = checkRange(percent, -100, 100);

    const { r, g, b } = HEXToRGB(hex);
    const hsl = RGBToHSL(r, g, b);
    const { h, s } = hsl;
    let { l } = hsl;
    const range = percent > 0 ? 100 - l : l;
    l += Math.round(range * 0.01 * percent);

    const { r: R, g: G, b: B } = HSLToRGB(h, s, l);

    return RGBToHEX(R, G, B);
  };

  const isWhite = (color: string) => {
    const { hex, check } = checkColor(color);
    if (!check) return false;
    const { r, g, b } = HEXToRGB(hex);
    const y = 0.2126 * r + 0.7152 * g + 0.0722 * b;
    return y > 128 + 64;
  };

  return { lightness, isWhite };
};

import { css, Theme } from "@emotion/react";
import { useColor } from "./hooks/useColor";
import { useCJoli } from "./hooks/useCJoli";

export const zoomIcon = () => css`
  transition: transform 0.1s;
  &:hover {
    transform: scale(1.2);
  }
`;

export const bgSecondary = ({ theme }: { theme: Theme }) =>
  css({
    backgroundColor: `${theme.colors.secondary} !important`,
    color: "white !important",
    textAlign: "center",
  });

export const useGlobal = (theme: Theme) => {
  const { lightness, isWhite } = useColor();
  const {
    theme: { primary },
  } = useCJoli();

  return css`
    body {
      background-color: ${theme.colors.primary};
      color: white;
      uuser-select: none;
    }
    input::placeholder {
      opacity: 0.5 !important;
    }
    span.badge.bg-secondary.menu {
      background-color: grey !important;
      opacity: 0.5;
    }
    .btn-primary {
      --bs-btn-bg: ${lightness(primary, 10)};
      --bs-btn-border-color: ${isWhite(primary)
        ? "black"
        : lightness(primary, 10)};
      --bs-btn-hover-bg: ${lightness(primary, isWhite(primary) ? -5 : 20)};
      --bs-btn-hover-border-color: ${isWhite(primary)
        ? "black"
        : lightness(primary, 20)};
      --bs-btn-active-bg: ${lightness(primary, isWhite(primary) ? -10 : 30)};
      --bs-btn-active-border-color: ${isWhite(primary)
        ? "black"
        : lightness(primary, 30)};
      --bs-btn-disabled-bg: ${lightness(primary, 10)};
      --bs-btn-color: ${isWhite(primary) ? "black" : "white"};
      --bs-btn-hover-color: ${isWhite(primary) ? "black" : "white"};
      --bs-btn-active-color: ${isWhite(primary) ? "black" : "white"};
      --bs-btn-disabled-color: ${isWhite(primary) ? "black" : "white"};
    }
    .progress {
      --bs-progress-bar-bg: ${theme.colors.secondary};
    }
    .accordion-button {
      --bs-accordion-active-bg: ${theme.colors.secondary};
      &:not(collapsed),
      &:not(collapsed)::after {
        --bs-accordion-active-color: white;
        --bs-body-color: white;
      }
    }
    .bg-secondary {
      --bs-bg-opacity: 1;
      --bs-secondary-rgb: 120, 129, 169;
    }
    .form-check-input:checked {
      background-color: ${lightness(primary, isWhite(primary) ? -50 : 10)};
      border-color: ${lightness(primary, isWhite(primary) ? -50 : 10)};
    }

