interface SimulationIconProps {
  show: boolean;
  title?: string;
  onRemove?: () => Promise<void>;
}

const SimulationIcon = ({ show, title, onRemove }: SimulationIconProps) => {
  const [open, setOpen] = React.useState(false);
  const { t } = useTranslation();
  const target = React.useRef(null);
  const handleRemove = async () => {
    if (onRemove) {
      await onRemove();
      setOpen(false);
    }
  };
  return (
    <>
      {show && !onRemove && (
        <OverlayTrigger overlay={<Tooltip>Simulation</Tooltip>}>
          <Badge className="mx-2" bg="secondary">
            <Alt />
          </Badge>
        </OverlayTrigger>
      )}
      {show && onRemove && (
        <>
          <Badge
            className="mx-2"
            bg="secondary"
            role="button"
            ref={target}
            onClick={() => setOpen(!open)}
          >
            <Alt />
          </Badge>
          <Overlay
            target={target.current}
            show={open}
            rootClose
            onHide={() => setOpen(false)}
          >
            {(props) => (
              <Popover {...props}>
                <Popover.Header style={{ color: "black" }}>
                  {title ?? t("simulation.tooltip", "Simulation")}
                </Popover.Header>
                <Popover.Body>
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      height: 30,
                    }}
                    className="mb-3"
                  >
                    <Trans i18nKey="simulation.removeAll">
                      Remove all simulations
                    </Trans>
                    <ButtonLoading variant="danger" onClick={handleRemove}>
                      <Trash />
                    </ButtonLoading>
