const PenaltyIcon = ({ positionId }: { positionId: number }) => {
  const [open, setOpen] = React.useState(false);
  const target = React.useRef(null);
  const { getPosition, loadRanking } = useCJoli();
  const uid = useUid();
  const { isAdmin } = useUser();
  const { t } = useTranslation();

