  const hasPenalty = position.penalty > 0;
  if (!hasPenalty && !isAdmin) {
    return <></>;
  }
  return (
    <>
      {!isAdmin && (
        <OverlayTrigger
          overlay={
            <Tooltip>
              {t("penalty.tooltip", { count: position.penalty })}
            </Tooltip>
          }
        >
          <MyBadge
            pill
            className="ms-2"
            data-penalty={hasPenalty}
            bg={hasPenalty ? "danger" : "warning"}
            role="button"
            ref={target}
            onClick={() => setOpen(!open)}
          >
            {position.penalty}P
          </MyBadge>
        </OverlayTrigger>
      )}
      {isAdmin && (
        <>
          <MyBadge
            pill
            className="ms-2"
            data-penalty={hasPenalty}
            bg={hasPenalty ? "danger" : "warning"}
            role="button"
            ref={target}
            onClick={() => setOpen(!open)}
          >
            {position.penalty}P
          </MyBadge>
          <Overlay
            target={target.current}
            show={open}
            rootClose
            onHide={() => setOpen(false)}
          >
            {(props) => (
              <Popover {...props}>
                <Popover.Body>
                  <Form onSubmit={handleSubmit(submit)}>
                    <Form.Group as={Row} controlId="penalty">
                      <Form.Label column>
                        <Trans i18nKey="penalty.label">Penalties</Trans>
                      </Form.Label>
                      <Col>
                        <Form.Control
                          type="number"
                          min={0}
                          max={99}
                          {...register("penalty")}
                        />
                      </Col>
                    </Form.Group>
                    <Form.Group as={Row} className="pt-2">
                      <Col sm={{ span: 10, offset: 2 }}>
                        <Button size="sm" className="mx-1" type="submit">
                          <Trans i18nKey="button.save">Save</Trans>
                        </Button>

                        <Button
                          variant="outline-secondary"
                          onClick={() => setOpen(false)}
                          size="sm"
                        >
                          <Trans i18nKey="button.close">Close</Trans>
                        </Button>
                      </Col>
                    </Form.Group>
                  </Form>
                </Popover.Body>
              </Popover>
            )}
          </Overlay>
        </>
      )}
    </>
  );
};

