import { CaretDownFill, CaretUpFill, PauseFill } from "react-bootstrap-icons";
import LeftCenterDiv from "../../../components/LeftCenterDiv";

interface CellValueProps<T> {
  value?: T;
  getLabel?: (a: T) => string | number | undefined;
  val: number | undefined;
  display: boolean;
}
const CellValue = <T,>({
  value,
  val,
  display,
  getLabel,
}: CellValueProps<T>) => {
  if (val == undefined || !display) {
    return "-";
  } else if (value && getLabel) {
    return getLabel(value);
  } else {
    return val;
  }
};

interface CellEvolutionProps {
  result: number;
  valB: number | undefined;
}

const CellEvolution = ({ result, valB }: CellEvolutionProps) => {
  if (result == 1) {
    return <CaretUpFill color="rgb(25, 135, 84)" className="mx-1" />;
  } else if (result == -1) {
    return <CaretDownFill color="rgb(220, 53, 69)" className="mx-1" />;
  } else if (!!valB && result == 0) {
    return <PauseFill style={{ transform: "rotate(90deg)" }} color="#ffc107" />;
  }
};

type TeamCellProps<T> = {
  value?: T;
  valueB?: T;
  call: (a: T) => number | undefined;
  getLabel?: (a: T) => string | number | undefined;
  getInfo?: (a: T) => string;
  up?: boolean;
  active: boolean;
  display: boolean;
};

const TeamCell = <T,>({
  value,
  valueB,
  call,
  getLabel,
  getInfo,
  up,
  active,
  display,
}: TeamCellProps<T>) => {
  const calcul = up
    ? (a: number, b: number) => (a > b ? 1 : a == b ? 0 : -1)
    : (a: number, b: number) => (a < b ? 1 : a == b ? 0 : -1);
  const val = value && call(value);
  const valB = valueB && call(valueB);
  const result =
    val == undefined || !valueB || valB == undefined ? 0 : calcul(val, valB);
  return (
    <td>
      <LeftCenterDiv width={40}>
        <CellValue
          val={val}
          value={value}
          getLabel={getLabel}
          display={display}
        />
        {active && <CellEvolution result={result} valB={valB} />}
        <span className="mx-1" style={{ fontSize: 11, color: "grey" }}>
          {getInfo && value && display && getInfo(value)}
        </span>
      </LeftCenterDiv>
    </td>
  );
};

export default TeamCell;

import { Table } from "react-bootstrap";
import TeamName from "../../../components/TeamName";
import CJoliTooltip from "../../../components/CJoliTooltip";
import React from "react";
import TeamCell from "./TeamCell";
import { Rank, Score, Squad, Team, Tourney } from "../../../models";
import { useCJoli } from "../../../hooks/useCJoli";
import { useTranslation } from "react-i18next";

const percent = (value: number, total: number) =>
  total == 0 ? "" : `${Math.round((value / total) * 100)}%`;

const average = (value: number, total: number) =>
  total == 0 ? "" : `avg: ${(value / total).toFixed(1)}`;

interface TeamTableProps {
  team: Team;
  teamB?: Team;
  squad?: Squad;
}

const useFormatRank = () => {
  const { t } = useTranslation();
  return (rank?: number) => {
    if (rank == undefined) return "";
    if (rank == 0) {
      return ` - ${t("rank.first", "1st")} 🥇`;
    } else if (rank == 1) {
      return ` - ${t("rank.second", "2ème")} 🥈`;
    } else if (rank == 2) {
      return ` - ${t("rank.third", "3rd")} 🥉`;
    } else {
      return ` - ${t("rank.rankth", { rank: rank + 1 })}`;
    }
  };
};

const useColumns = (tourney?: Tourney, teamB?: Team) => {
  const { t } = useTranslation();
  const formatRank = useFormatRank();

  const winPt = tourney?.config.win || 2;

  let columns = [
    {
      label: "Rang",
      description: t("rank.ranking", "Ranking"),
      callRank: (r: Rank) => r.order || 0,
      getLabel: (r: Rank) =>
        r.order == 1
          ? `${t("rank.first")} 🥇`
          : r.order == 2
            ? `${t("rank.second")} 🥈`
            : r.order == 3
              ? `${t("rank.third")} 🥉`
              : t("rank.rankth", { rank: r.order }),
      up: false,
      active: true,
      needTeam: true,
    },
    {
      label: "PTS",
      description: t("rank.total", "Points"),
      callScore: (s: Score) => s.total,
      getInfo: (s: Score) => percent(s.total, winPt * s.game),
      up: true,
      active: !!teamB,
      needTeam: true,
    },
    {
      label: "PJ",
      description: t("rank.game", "Games played"),
      callScore: (s: Score) => s.game,
      up: true,
      active: false,
      needTeam: false,
    },
    {
      label: "V",
      description: t("rank.win", "Victories"),
      callScore: (s: Score) => s.win,
      getInfo: (s: Score) =>
        percent(s.win, s.game) + formatRank(s.ranks?.win?.rank),
      up: true,
      active: !!teamB,
      needTeam: false,
    },
    {
      label: "N",
      description: t("rank.neutral", "Drawn games"),
      callScore: (s: Score) => s.neutral,
      getInfo: (s: Score) =>
        percent(s.neutral, s.game) + formatRank(s.ranks?.neutral?.rank),
      up: true,
      active: false,
      needTeam: false,
    },
    {
      label: "D",
      description: t("rank.loss", "Defeats"),
      callScore: (s: Score) => s.loss,
      getInfo: (s: Score) =>
        percent(s.loss, s.game) + formatRank(s.ranks?.loss?.rank),
      up: false,
      active: !!teamB,
      needTeam: true,
    },
    {
      label: "BP",
      description: t("rank.goalFor", "Goals for"),
      callScore: (s: Score) => {
        if (!teamB) return s.goalFor / s.game;
        return s.goalFor;
      },
      getLabel: (s: Score) => s.goalFor,
      getInfo: (s: Score) =>
        average(s.goalFor, s.game) + formatRank(s.ranks?.goalFor?.rank),
      up: true,
      active: true,
      needTeam: false,
    },
    {
      label: "BC",
      description: t("rank.goalAgainst", "Goals against"),
      callScore: (s: Score) => {
        if (!teamB) return s.goalAgainst / s.game;
        return s.goalAgainst;
      },
      getLabel: (s: Score) => s.goalAgainst,
      getInfo: (s: Score) =>
        average(s.goalAgainst, s.game) + formatRank(s.ranks?.goalAgainst?.rank),
      up: false,
      active: true,
      needTeam: false,
    },
    {
      label: "BL",
      description: t("rank.shutOut", "ShutOut"),
      callScore: (s: Score) => {
        if (!teamB) return s.shutOut / s.game;
        return s.shutOut;
      },
      getLabel: (s: Score) => s.shutOut,
      getInfo: (s: Score) =>
        percent(s.shutOut, s.game) + formatRank(s.ranks?.shutOut?.rank),
      up: true,
      active: true,
      needTeam: false,
    },
    {
      label: "+/-",
      description: t("rank.goalDiff", "Goal average"),
      callScore: (s: Score) => {
        if (!teamB) return s.goalDiff / s.game;
        return s.goalDiff;
      },
      getLabel: (s: Score) => s.goalDiff,
      getInfo: (s: Score) =>
        average(s.goalDiff, s.game) + formatRank(s.ranks?.goalDiff?.rank),
      up: true,
      active: true,
      needTeam: false,
    },
  ];
  if (tourney?.config?.hasPenalty) {
    columns = [
      ...columns,
      {
        label: "PEN",
        description: t("rank.penalty", "Penalties"),
        callScore: (s: Score) => s.penalty,
        up: false,
        active: true,
        needTeam: false,
      },
    ];
  }

  return columns;
};

const TeamTable = ({ team, teamB, squad }: TeamTableProps) => {
  const { ranking, tourney, getTeamRank, getScoreForTeam, getScoreFromSquad } =
    useCJoli();
  let rank = getTeamRank(team);
  let rankB = teamB && getTeamRank(teamB);
  const score = getScoreForTeam(team)!;
  let scoreB = teamB && getScoreForTeam(teamB);
  const scoreSquad = squad && getScoreFromSquad(squad, team);
  const scoreSquadB = squad && teamB && getScoreFromSquad(squad, teamB);
  if ((!rank || !rankB) && scoreSquad && scoreSquadB) {
    rank = { order: scoreSquad.rank } as Rank;
    rankB = { order: scoreSquadB.rank } as Rank;
  }

  let datas: { team?: Team; rank?: Rank; score?: Score }[] = [
    { team, rank, score },
  ];
  if (teamB) {
    datas = [...datas, { team: teamB, rank: rankB, score: scoreB }];
  } else {
    const scoreTourney = ranking?.scores.scoreTourney;
    scoreB = scoreTourney;
    datas = [
      ...datas,
      { team: undefined, rank: undefined, score: scoreTourney },
    ];
  }

  const columns = useColumns(tourney, teamB);

  return (
    <Table striped bordered size="sm" style={{ textAlign: "center" }}>
      <thead>
        <tr>
          <th />
          {datas.map(({ team }) => (
            <th key={team?.id || 0}>
              {team ? <TeamName teamId={team.id} /> : tourney?.name}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {columns.map((c, i) => (
          <tr key={i}>
            <td>
              <CJoliTooltip info={c.description}>{c.label}</CJoliTooltip>
            </td>
            {datas.map(({ team, rank, score }, j) => (
              <React.Fragment key={j}>
                {c.callRank ? (
                  <TeamCell
                    value={rank}
                    valueB={rankB}
                    call={c.callRank}
                    getLabel={c.getLabel}
                    getInfo={c.getInfo}
                    active={c.active && j == 0}
                    up={c.up}
                    display={!c.needTeam || !!team}
                  />
                ) : (
                  <TeamCell
                    value={score}
                    valueB={scoreB}
                    call={c.callScore!}
                    getLabel={c.getLabel}
                    getInfo={c.getInfo}
                    active={c.active && j == 0}
                    up={c.up}
                    display={!c.needTeam || !!team}
                  />
                )}
              </React.Fragment>
            ))}
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

