import React from "react";
import { Spinner } from "react-bootstrap";
import { CheckCircle, XCircle } from "react-bootstrap-icons";

interface ScoreButtonProps {
  id: string;
  action: "remove" | "save";
  onClick: () => Promise<void>;
}

const ScoreButton = ({ id, action, onClick }: ScoreButtonProps) => {
  const [loading, setLoading] = React.useState(false);
  const handleClick = async () => {
    setLoading(true);
    await onClick();
    setLoading(false);
  };
  return (
    <>
      {action == "save" && !loading && (
        <CheckCircle
          color="rgb(25, 135, 84)"
          size={32}
          role="button"
          onClick={handleClick}
          data-testid={id}
        />
      )}
      {action == "remove" && !loading && (
        <XCircle
          color="rgb(220, 53, 69)"
          size={32}
          role="button"
          onClick={handleClick}
          data-testid={id}
        />
      )}
      {loading && (
        <Spinner
          animation="grow"
          variant={action == "remove" ? "danger" : "success"}
        />
      )}
    </>
  );
};

export default ScoreButton;

import { Badge } from "react-bootstrap";
import { IMatch } from "../../../models";

interface ScoreCellViewProps {
  match: IMatch;
  mode: "A" | "B";
}

const ScoreCellView = ({ match, mode }: ScoreCellViewProps) => {
  let badge =
    (mode == "A" && match.scoreA > match.scoreB) ||
    (mode == "B" && match.scoreA < match.scoreB)
      ? "success"
      : match.scoreA === match.scoreB
      ? "warning"
      : "danger";
  if (match.forfeitA) {
    badge = mode == "A" ? "danger" : "success";
  } else if (match.forfeitB) {
    badge = mode == "A" ? "success" : "danger";
  }
  const text = badge == "warning" ? "black" : "white";

  return (
    <Badge bg={badge} text={text} style={{ fontSize: "18px" }} className="my-1">
      {mode == "A" ? match.scoreA : match.scoreB}
    </Badge>
  );
};

