interface MatchesStackProps extends JSX.IntrinsicAttributes {
  phase: Phase;
}
const MatchesStack = ({ phase }: MatchesStackProps) => {
  const { matches, loadRanking, daySelected, selectDay } = useCJoli();
  const uid = useUid();
  const { register, getValues } =
    useForm<Record<string, { scoreA: number | ""; scoreB: number | "" }>>();
  const { setShow } = useModal("blockShot");
  const { squadId } = useParams();
  const { t } = useTranslation();

  const filter = useCallback(
    (match: Match) =>
      match.phaseId == phase.id &&
      (!squadId || parseInt(squadId) == match.squadId),
    [phase, squadId]
  );

  const datas = useMemo(() => {
    const matchesFiltered = matches.filter(filter);

    const datas = matchesFiltered.reduce<Record<string, Match[]>>((acc, m) => {
      const time = dayjs(m.time);
      const date = time.format("YYYY-MM-DD");
      const list = [...(acc[date] || []), m];
      list.sort((a, b) => {
        return a.location && b.location && a.location > b.location ? 1 : -1;
      });
      return { ...acc, [date]: list };
    }, {});
    return datas;
  }, [filter, matches]);

  const matchesNotDone = useMemo(
    () => matches.filter(filter).filter((m) => !m.done),
    [filter, matches]
  );

  const keys = useMemo(() => {
    const keys = Object.keys(datas);
    keys.sort();
    return keys;
  }, [datas]);

  useEffect(() => {
    if (matchesNotDone.length > 0) {
      const time = dayjs(matchesNotDone[0].time);
      const date = time.format("YYYY-MM-DD");
      selectDay(date);
    } else {
      keys && keys.length > 0 && selectDay(keys[0]);
    }
  }, [keys, selectDay, datas, matchesNotDone]);

  const upperFirstLetter = (value: string) => {
    return value.charAt(0).toUpperCase() + value.slice(1);
  };

  const saveMatch = async (match: Match) => {
    let { scoreA, scoreB } = getValues(`m${match.id}`);
    if (scoreA == "") scoreA = 0;
    if (scoreB == "") scoreB = 0;

    if (match.forfeitA || match.forfeitB) {
      scoreA = 0;
      scoreB = 0;
    }
    if (match.shot && scoreA == scoreB) {
      setShow(true);
      return;
    }
    const ranking = await cjoliService.saveMatch(uid, {
      ...match,
      scoreA,
      scoreB,
    });
    loadRanking(ranking);
  };

  const clearMatch = async (match: Match) => {
    const ranking = await cjoliService.clearMatch(uid, match);
    loadRanking(ranking);
  };

  return (
    <CJoliStack gap={0} className="col-md-8 mx-auto mt-5" data-testid="matches">
      <div className="p-2">
        <CJoliCard>
          <Loading ready={!!matches}>
            <Accordion
              activeKey={daySelected}
              onSelect={(e) => {
                selectDay(e as string);
              }}
            >
              {keys.map((key, index) => {
                const datasOrder = datas[key];
                datasOrder.sort((a, b) => (a.time > b.time ? 1 : -1));
                const map = datasOrder.reduce<Record<string, Match[]>>(
                  (acc, m) => {
                    const key = dayjs(m.time).format("LT");
                    return { ...acc, [key]: [...(acc[key] || []), m] };
                  },
                  {}
                );
                return (
                  <Accordion.Item key={index} eventKey={key} data-testid={key}>
                    <Accordion.Header>
                      {upperFirstLetter(dayjs(key).format("dddd LL"))}
                    </Accordion.Header>
                    <Accordion.Body>
                      <Table
                        striped
                        bordered
                        hover
                        style={{ textAlign: "center" }}
                      >
                        <tbody>
                          {Object.keys(map).map((k) =>
                            map[k].map((match, i) => {
                              return (
                                <MatchRow
                                  key={match.id}
                                  index={i}
                                  match={match}
                                  rowSpan={map[k].length}
                                  saveMatch={saveMatch}
                                  clearMatch={clearMatch}
                                  register={register}
                                />
                              );
                            })
                          )}
                        </tbody>
                      </Table>
                    </Accordion.Body>
                  </Accordion.Item>
                );
              })}
            </Accordion>
          </Loading>
        </CJoliCard>
      </div>
      <InfoModal
        id="blockShot"
        title={t("match.blockShot.title", "Save Score")}
        variant="danger"
      >
        <p>
          <Trans i18nKey="match.blockShot.error">
            Unable to record the score.
            <br />
            The match must end in a penalty shootout.
            <br />
            Please indicate a winner.
          </Trans>
        </p>
      </InfoModal>
    </CJoliStack>
  );
};

export const MatchesStackMemo = memo(MatchesStack);
export default MatchesStack;

import { Card, Table } from "react-bootstrap";
import CJoliCard from "../../components/CJoliCard";
import { Trans } from "react-i18next";
import LeftCenterDiv from "../../components/LeftCenterDiv";
import TeamName from "../../components/TeamName";
import { useCJoli } from "../../hooks/useCJoli";
import { useCallback } from "react";
import { Rank } from "../../models";
import ButtonTeam from "../../components/ButtonTeam";

const RankTable = () => {
  const { tourney, getRankPosition, findTeam } = useCJoli();

  const ranks = tourney?.ranks || [];

  const formatRank = useCallback((rank: Rank) => {
    switch (rank.order) {
      case 1:
        return "🥇";
      case 2:
        return "🥈";
      case 3:
        return "🥉";
      default:
        return rank.order;
    }
  }, []);

  return (
    <div className="p-2">
      <CJoliCard>
        <Card.Body>
          <Card.Title>
            <Trans i18nKey="rank.title">Ranking</Trans>
          </Card.Title>
          <Table
            striped
            bordered
            hover
            responsive
            size="sm"
            style={{ textAlign: "center" }}
          >
            <thead>
              <tr>
                <th style={{ width: "20%" }}>#</th>
                <th className="w-50">
                  <Trans i18nKey="rank.team">Team</Trans>
                </th>
              </tr>
            </thead>
            <tbody>
              {ranks.map((rank) => {
                const positionId = getRankPosition(rank);
                const team = findTeam({ positionId });
                return (
                  <tr key={rank.id}>
                    <td>{formatRank(rank)}</td>
                    <td>
                      <LeftCenterDiv>
                        <TeamName
                          positionId={positionId || 0}
                          defaultName={rank.name}
                        />
                        {team && <ButtonTeam team={team} />}
                      </LeftCenterDiv>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
        </Card.Body>
      </CJoliCard>
    </div>
  );
};

export default RankTable;

import CJoliStack from "../../components/CJoliStack";
import RankTable from "../rank/RankTable";

const RankStack = () => {
  return (
    <CJoliStack gap={0} className="col-md-8 mx-auto mt-5" data-testid="rank">
      <div className="p-2">
        <RankTable />
      </div>
    </CJoliStack>
  );
};

export default RankStack;

import RankingStack from "./home/RankingStack";
import MatchesStack from "./home/MatchesStack";
import Loading from "../components/Loading";
import { useParams } from "react-router-dom";
import { useCJoli } from "../hooks/useCJoli";
import useUid from "../hooks/useUid";
//import SummaryStack from "./home/SummaryStack";
import { useQuery } from "@tanstack/react-query";
import { useServer } from "../hooks/useServer";
import { useEffect, useState } from "react";
import CJoliStack from "../components/CJoliStack";
import CJoliCard from "../components/CJoliCard";
import { Alert } from "react-bootstrap";
import { Trans } from "react-i18next";
import { useApi } from "../hooks/useApi";
import RankStack from "./home/RankStack";

const HomePage = () => {
  const { phases, matches } = useCJoli("home");
  const { sendMessage, register } = useServer();
  const { getRanking } = useApi();
  const uid = useUid();
  const { phaseId } = useParams();
  const [loading, setLoading] = useState(true);

  const { refetch, isFetching } = useQuery(getRanking(uid));

  useEffect(() => {
    !isFetching && sendMessage({ type: "selectTourney", uid });
    !isFetching && setLoading(false);
  }, [uid, isFetching, sendMessage]);

  useEffect(() => {
    register("updateRanking", async () => {
      refetch();
    });
  }, [refetch, register]);

  let phase =
    phases && phases.find((p) => phaseId && p.id == parseInt(phaseId));
  if (!phase && phases && phases?.length > 0) {
    phase = phases[0];
  }
  const allMatchesDone = matches.length > 0 && matches.every((m) => m.done);
  return (
    <Loading ready={!loading}>
      {allMatchesDone && <RankStack />}
      {phase && <RankingStack phase={phase} />}
      {phase && <MatchesStack phase={phase} />}
      {!phase && (
        <CJoliStack gap={0} className="col-md-8 mx-auto mt-5">
          <div className="p-2">
            <CJoliCard>
              <Alert variant="warning" className="mb-0">
                <Trans i18nKey="home.tourneyNotConfigured">
                  Tourney not configured
                </Trans>
              </Alert>
            </CJoliCard>
          </div>
        </CJoliStack>
      )}
    </Loading>
  );
};

export default HomePage;
