export enum CJoliActions {
  LOAD_TOURNEYS = "LOAD_TOURNEYS",
  SELECT_TOURNEY = "SELECT_TOURNEY",
  LOAD_RANKING = "LOAD_RANKING",
  SELECT_DAY = "SELECT_DAY",
  LOAD_TOURNEY = "LOAD_TOURNEY",
  SET_COLOR = "SET_COLOR",
  SELECT_PAGE = "SELECT_PAGE",
}

export enum UserActions {
  LOAD_USER = "LOAD_USER",
  COUNT_USER = "COUNT_USER",
}

import React, { Dispatch } from "react";
import {
  Match,
  Phase,
  Position,
  Ranking,
  Squad,
  Team,
  Tourney,
  TypePage,
} from "../models";
import { CJoliActions } from "./actions";
import dayjs from "dayjs";

interface CJoliState {
  tourneys?: Tourney[];
  tourney?: Tourney;
  ranking?: Ranking;
  teams?: Team[];
  phases?: Phase[];
  squads?: Squad[];
  positions?: Position[];
  matches: Match[];
  daySelected: string;
  theme: {
    primary: string;
    secondary: string;
  };
  page: TypePage;
}

export const CJoliContext = React.createContext<{
  state: CJoliState;
  dispatch: Dispatch<Action>;
} | null>(null);

const initialState: CJoliState = {
  daySelected: dayjs().format("YYYY-MM-DD"),
  matches: [],
  theme: {
    primary: "#202644",
    secondary: "#932829",
  },
  page: "welcome",
};

type Action =
  | {
      type: CJoliActions.LOAD_TOURNEYS;
      payload: Tourney[];
    }
  | {
      type: CJoliActions.SELECT_TOURNEY;
      payload: Tourney;
    }
  | {
      type: CJoliActions.LOAD_RANKING;
      payload: Ranking;
    }
  | { type: CJoliActions.SELECT_DAY; payload: string }
  | { type: CJoliActions.LOAD_TOURNEY; payload: Tourney }
  | {
      type: CJoliActions.SET_COLOR;
      payload: { primary: string; secondary: string };
    }
  | {
      type: CJoliActions.SELECT_PAGE;
      payload: TypePage;
    };

const reducer = (state: CJoliState, action: Action) => {
  switch (action.type) {
    case CJoliActions.LOAD_TOURNEYS: {
      return { ...state, tourneys: action.payload };
    }
    case CJoliActions.LOAD_TOURNEY: {
      const tourney = action.payload;
      const teams = tourney.teams;
      teams.sort((a, b) => (a.name > b.name ? 1 : -1));
      return { ...state, tourney: tourney };
    }
    case CJoliActions.SELECT_TOURNEY: {
      return {
        ...state,
        tourney: action.payload,
      };
    }
    case CJoliActions.LOAD_RANKING: {
      const ranking = action.payload;
      const tourney = ranking.tourney;
      const teams = tourney.teams;
      teams.sort((a, b) => (a.name > b.name ? 1 : -1));
      const phases = tourney.phases;
      const squads = phases.reduce<Squad[]>(
        (acc, phase) => [...acc, ...phase.squads],
        []
      );
      const positions = squads.reduce<Position[]>(
        (acc, squad) => [...acc, ...squad.positions],
        []
      );
      const matches = squads.reduce<Match[]>(
        (acc, squad) => [...acc, ...squad.matches],
        []
      );
      return {
        ...state,
        ranking,
        tourney,
        teams,
        phases,
        squads,
        positions,
        matches,
      };
    }
    case CJoliActions.SELECT_DAY: {
      return {
        ...state,
        daySelected: action.payload,
      };
    }
    case CJoliActions.SET_COLOR: {
      return { ...state, theme: action.payload };
    }
    case CJoliActions.SELECT_PAGE: {
      return { ...state, page: action.payload };
    }
  }
};

export const CJoliProvider = ({ children }: { children: React.ReactNode }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  return (
    <CJoliContext.Provider
      value={{
        state,
        dispatch,
      }}
    >
      {children}
    </CJoliContext.Provider>
  );
};

import React, { Dispatch } from "react";
import { User } from "../models";
import { UserActions } from "./actions";

interface UserState {
  user?: User;
  countUser: number;
}

export const UserContext = React.createContext<{
  state: UserState;
  dispatch: Dispatch<Action>;
} | null>(null);

const initialState: UserState = { countUser: 0 };

type Action =
  | {
      type: UserActions.LOAD_USER;
      payload?: User;
    }
  | { type: UserActions.COUNT_USER; payload: number };

const reducer = (state: UserState, action: Action) => {
  switch (action.type) {
    case UserActions.LOAD_USER: {
      const user = action.payload;
      return { ...state, user };
    }
    case UserActions.COUNT_USER:
      return { ...state, countUser: action.payload };
  }
  return state;
};

export const UserProvider = ({ children }: { children: React.ReactNode }) => {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  return (
    <UserContext.Provider
      value={{
        state,
        dispatch,
      }}
    >
      {children}
    </UserContext.Provider>
  );
};

import React, { ReactNode } from "react";

type Type = "danger" | "success";

interface ToastState {
  show: boolean;
  type: Type;
  message: string;
}

export const ToastContext = React.createContext<{
  state: ToastState;
  hideToast: () => void;
  showToast: (type: Type, message: string) => void;
} | null>(null);

export const ToastProvider = ({ children }: { children: ReactNode }) => {
  const [state, setState] = React.useState<ToastState>({
    show: false,
    type: "success",
    message: "",
  });
  const showToast = async (type: Type, message: string) => {
    if (state.show) {
      hideToast();
      await new Promise((resolve) => setTimeout(resolve, 300));
    }
    setState({ show: true, type, message });
  };
  const hideToast = () => {
    setState({ ...state, show: false });
  };
  return (
    <ToastContext.Provider value={{ state, showToast, hideToast }}>
      {children}
    </ToastContext.Provider>
  );
};

import { createContext, ReactNode, useState } from "react";

export const ModalContext = createContext<{
  show: Record<string, boolean>;
  setShow: (show: Record<string, boolean>) => void;
  data: Record<string, object>;
  setData: (data: Record<string, object>) => void;
} | null>(null);

export const ModalProvider = ({ children }: { children: ReactNode }) => {
  const [show, setShow] = useState<Record<string, boolean>>({});
  const [data, setData] = useState<Record<string, object>>({});
  return (
    <ModalContext.Provider value={{ show, setShow, data, setData }}>
      {children}
    </ModalContext.Provider>
  );
};

import styled from "@emotion/styled";
import { Card } from "react-bootstrap";

const CJoliCard = styled(Card)`
  background-color: white;

  border: 3px solid ${(props) => props.theme.colors.secondary};
  & > .card-body {
    background-color: #e7e9ee;
  }
  & .card-title {
    color: ${(props) => props.theme.colors.secondary};
  }
`;

export default CJoliCard;

import styled from "@emotion/styled";
import { Stack } from "react-bootstrap";

const CJoliStack = styled(Stack)`
  background-color: white;
  color: black;
  margin-bottom: 20px;
`;

export default CJoliStack;

import styled from "@emotion/styled";
import { ReactNode } from "react";
import { Container } from "react-bootstrap";

const MyProgressBar = styled("div")`
  height: 4px;
  width: 100%;
  --c: no-repeat linear-gradient(#6100ee 0 0);
  background: var(--c), var(--c), #d7b8fc;
  background-size: 60% 100%;
  animation: l16 3s infinite;
  @keyframes l16 {
    0% {
      background-position: -150% 0, -150% 0;
    }
    66% {
      background-position: 250% 0, -150% 0;
    }
    100% {
      background-position: 250% 0, 250% 0;
    }
  }
`;

const Waiting = ({
  ready,
  children,
}: {
  ready: boolean;
  children: ReactNode;
}) => {
  return (
    <>
      {!ready && (
        <Container className="p-4" fluid>
          <MyProgressBar />
        </Container>
      )}
      {ready && children}
    </>
  );
};

export default Waiting;

import { useCallback, useContext, useEffect } from "react";
import { CJoliContext } from "../contexts/CJoliContext";
import {
  Match,
  Phase,
  Rank,
  Ranking,
  Squad,
  Team,
  Tourney,
  TypePage,
} from "../models";
import { CJoliActions } from "../contexts/actions";

export const useCJoli = (page?: TypePage) => {
  const ctx = useContext(CJoliContext);
  if (!ctx) {
    throw new Error("useCJoli has to be used within <CJoliProvider>");
  }

  const { state, dispatch } = ctx;
  const loadTourneys = useCallback(
    (tourneys: Tourney[]) =>
      dispatch({ type: CJoliActions.LOAD_TOURNEYS, payload: tourneys }),
    [dispatch]
  );
  const selectTourney = useCallback(
    (tourney: Tourney) =>
      dispatch({ type: CJoliActions.SELECT_TOURNEY, payload: tourney }),
    [dispatch]
  );

  const loadRanking = useCallback(
    (ranking: Ranking) =>
      dispatch({ type: CJoliActions.LOAD_RANKING, payload: ranking }),
    [dispatch]
  );

  const loadTourney = useCallback(
    (tourney: Tourney) =>
      dispatch({ type: CJoliActions.LOAD_TOURNEY, payload: tourney }),
    [dispatch]
  );

  const getSquad = useCallback(
    (squadId: number) => {
      const squad = state.squads?.find((s) => s.id === squadId);
      return squad;
    },
    [state]
  );
  const getTeam = useCallback(
    (teamId: number) => state.teams?.find((t) => t.id === teamId),
    [state.teams]
  );
  const getPosition = useCallback(
    (positionId: number) => state.positions?.find((p) => p.id === positionId),
    [state.positions]
  );
  const findTeam = useCallback(
    ({ positionId }: { positionId?: number }) => {
      if (positionId) {
        const position = getPosition(positionId);
        return position && getTeam(position.teamId);
      }
      return undefined;
    },
    [getPosition, getTeam]
  );
  const getTeamInfo = useCallback(
    (positionId: number, defaultName?: string) => {
      const position = getPosition(positionId);
      if (!position) return { name: defaultName };
      const team = getTeam(position.teamId);
      if (!team) {
        return { name: (defaultName ?? position.name) || "noname" };
      }
      const name = position.name
        ? `${team?.name} - ${position.short}`
        : team?.name || "noname";
      return { name, logo: team?.logo };
    },
    [getPosition, getTeam]
  );

  const getScoreFromPosition = useCallback(
    (positionId: number, squadId: number) => {
      const scoreSquad = state.ranking?.scores.scoreSquads.find(
        (s) => s.squadId == squadId
      );
      return scoreSquad?.scores.find((s) => s.positionId == positionId);
    },
    [state.ranking]
  );

  const getRankPosition = useCallback(
    (rank: Rank) => {
      const scoreSquad = state.ranking?.scores.scoreSquads.find(
        (s) => s.squadId == rank.squadId
      );
      const score = scoreSquad?.scores[rank.value - 1];
      return score?.positionId;
    },
    [state.ranking]
  );
  const getTeamRank = useCallback(
    (team: Team) => {
      for (const rank of state.tourney?.ranks || []) {
        const positionId = getRankPosition(rank);
        if (!positionId) {
          continue;
        }
        const position = getPosition(positionId);
        if (position?.teamId == team.id) {
          return rank;
        }
      }
      return undefined;
    },
    [getPosition, getRankPosition, state.tourney?.ranks]
  );

  const isTeamInPhase = useCallback((teamId: number, phase: Phase) => {
    return phase.squads.some((squad) =>
      squad.positions.some((position) => position.teamId == teamId)
    );
  }, []);
  const isTeamInSquad = useCallback((teamId: number, squad: Squad) => {
    return squad.positions.some((position) => position.teamId == teamId);
  }, []);
  const isTeamInMatch = useCallback(
    (teamId: number, match: Match) => {
      return (
        getPosition(match.positionIdA)?.teamId == teamId ||
        getPosition(match.positionIdB)?.teamId == teamId
      );
    },
    [getPosition]
  );

  const getScoreForTeam = useCallback(
    (team: Team) => {
      return state.ranking?.scores.scoreTeams[team.id];
    },
    [state.ranking?.scores.scoreTeams]
  );

  const getScoreFromSquad = useCallback(
    (squad: Squad, team: Team) => {
      const scoreSquad = state.ranking?.scores.scoreSquads.find(
        (s) => s.squadId == squad.id
      );
      return scoreSquad?.scores.find((s) => s.teamId == team.id);
    },
    [state]
  );

  const selectDay = useCallback(
    (day: string) => dispatch({ type: CJoliActions.SELECT_DAY, payload: day }),
    [dispatch]
  );

  const setColor = useCallback(
    (primary: string, secondary: string) =>
      dispatch({
        type: CJoliActions.SET_COLOR,
        payload: { primary, secondary },
      }),
    [dispatch]
  );

  const selectPage = useCallback(
    (page: TypePage) =>
      dispatch({ type: CJoliActions.SELECT_PAGE, payload: page }),
    [dispatch]
  );

  useEffect(() => {
    page && selectPage(page);
  }, [page, selectPage]);

  return {
    ...state,
    loadRanking,
    loadTourneys,
    loadTourney,
    selectTourney,
    getSquad,
    getTeam,
    findTeam,
    getPosition,
    getTeamInfo,
    getRankPosition,
    getTeamRank,
    isTeamInPhase,
    isTeamInSquad,
    isTeamInMatch,
    getScoreForTeam,
    selectDay,
    getScoreFromPosition,
    getScoreFromSquad,
    setColor,
    selectPage,
    isHomePage: state.page == "home",
  };
};

import { useParams } from "react-router-dom";

const useUid = () => {
  const { uid } = useParams();

  const host = window.location.host;
  const uidDomain = host.split(".")[0];
  if (
    uidDomain &&
    uidDomain != "www" &&
    uidDomain != "cjoli-hockey" &&
    !uidDomain.startsWith("localhost")
  ) {
    return uidDomain;
  }

  return uid!;
};

export default useUid;

import { useState, useEffect } from "react";

const useScreenSize = () => {
  const [screenSize, setScreenSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => {
      setScreenSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);
  return {
    ...screenSize,
    isMobile: screenSize.width < 1200,
  };
};

export default useScreenSize;

import { useCallback, useContext } from "react";
import { UserContext } from "../contexts/UserContext";
import { User, UserConfig } from "../models";
import { UserActions } from "../contexts/actions";
import { useCJoli } from "./useCJoli";
import * as cjoliService from "../services/cjoliService";
import useUid from "./useUid";

export const useUser = () => {
  const ctx = useContext(UserContext);
  const { loadRanking } = useCJoli();
  const uid = useUid();
  if (!ctx) {
    throw new Error("useUser has to be used within <UserProvider>");
  }

  const { state, dispatch } = ctx;
  const loadUser = useCallback(
    (user?: User) => dispatch({ type: UserActions.LOAD_USER, payload: user }),
    [dispatch]
  );
  const setCountUser = useCallback(
    (count: number) =>
      dispatch({ type: UserActions.COUNT_USER, payload: count }),
    [dispatch]
  );

  const handleSaveUserConfig = useCallback(
    async (userConfig: UserConfig) => {
      try {
        const ranking = await cjoliService.saveUserConfig(uid, userConfig);
        loadRanking(ranking);
        const configs =
          state.user?.configs?.map((c) =>
            c.tourneyId == userConfig.tourneyId ? userConfig : c
          ) || [];
        loadUser({ ...state.user, configs } as User);
      } catch (error) {
        console.error("Unable to save userConfig", error);
      }
    },
    [loadRanking, loadUser, uid, state.user]
  );

  const { tourney } = useCJoli();

  return {
    ...state,
    isConnected: state.user,
    isAdmin: state.user?.role === "ADMIN",
    userConfig: state.user?.configs?.find(
      (c) => c.tourneyId == tourney?.id
    ) || { tourneyId: 0, useCustomEstimate: false, favoriteTeamId: 0 },
    loadUser,
    setCountUser,
    handleSaveUserConfig,
  };
};

import React, { ReactNode } from "react";
import { IMatch, Match, Team } from "../../../models";
import { FieldValues, UseFormRegister } from "react-hook-form";

interface MatchRowProps {
  match: Match;
  imatch: IMatch;
  saveMatch: (match: Match) => Promise<void>;
  clearMatch: (match: Match) => Promise<void>;
  register: UseFormRegister<FieldValues>;
  teamA?: Team;
  teamB?: Team;
  done: boolean;
  isSimulation: boolean;
}

export const MatchRowContext = React.createContext<MatchRowProps | null>(null);

export const MatchRowProvider = ({
  match,
  imatch,
  saveMatch,
  clearMatch,
  register,
  teamA,
  teamB,
  done,
  isSimulation,
  children,
}: MatchRowProps & {
  children: ReactNode;
}) => {
  return (
    <MatchRowContext.Provider
      value={{
        match,
        imatch,
        saveMatch,
        clearMatch,
        register,
        teamA,
        teamB,
        done,
        isSimulation,
      }}
    >
      {children}
    </MatchRowContext.Provider>
  );
};

import { useContext } from "react";
import { MatchRowContext } from "./MatchRowContext";

export const useMatchRow = () => {
  const ctx = useContext(MatchRowContext);
  if (!ctx) {
    throw new Error("useMatchRow has to be used within <MatchRowProvider>");
  }
  return { ...ctx };
};

import React from "react";
import { OverlayTrigger, Tooltip } from "react-bootstrap";

const CJoliTooltip = ({
  children,
  info,
}: {
  children: React.ReactNode;
  info: string;
}) => {
  return (
    <OverlayTrigger overlay={<Tooltip>{info}</Tooltip>}>
      <span className="user-select-none">{children}</span>
    </OverlayTrigger>
  );
};

export default CJoliTooltip;

export const useColor = () => {
  const checkRange = (count: number, start: number, end: number) => {
    if (count < start) return start;
    if (count > end) return end;
    return count;
  };

  const getFullHEX = (hex: string) => {
    const [r, g, b] = hex.slice(1, 4).split("");
    return `#${r}${r}${g}${g}${b}${b}`;
  };

  const checkColor = (color: string) => {
    if (typeof color !== "string") return { hex: color, check: false };
    color = color.toLowerCase();
    const isHEX = color.slice(0, 1) === "#";

    if (isHEX) {
      const validHEX = color.search(/^#[a-f0-9]{3}([a-f0-9]{3})?$/) !== -1;
      return {
        hex: validHEX && color.length === 4 ? getFullHEX(color) : color,
        check: validHEX,
      };
    }
    return { hex: color, check: false };
  };

  const checkRGB = (r: number, g: number, b: number) => {
    r = checkRange(r, 0, 255);
    g = checkRange(g, 0, 255);
    b = checkRange(b, 0, 255);
    return { r, g, b };
  };

  const HEXToRGB = (hex: string) => ({
    r: parseInt(hex.slice(1, 3), 16),
    g: parseInt(hex.slice(3, 5), 16),
    b: parseInt(hex.slice(5, 7), 16),
  });

  const calculH = (
    delta: number,
    max: number,
    { r, g, b }: { r: number; g: number; b: number }
  ) => {
    let h = 0;
    if (delta === 0) {
      h = 0;
    } else if (max === r) {
      h = ((g - b) / delta) % 6;
    } else if (max === g) {
      h = (b - r) / delta + 2;
    } else if (max === b) {
      h = (r - g) / delta + 4;
    }

    h = Math.round(h * 60);
    h = h < 0 ? h + 360 : h;
    return h;
  };

  const RGBToHSL = (r: number, g: number, b: number) => {
    r /= 255;
    g /= 255;
    b /= 255;

    const min = Math.min(r, g, b);
    const max = Math.max(r, g, b);
    const delta = max - min;
    let [h, s, l] = [0, 0, 0];

    h = calculH(delta, max, { r, g, b });

    l = (max + min) / 2;
    s = delta === 0 ? 0 : delta / (1 - Math.abs(2 * l - 1));

    l = +(l * 100).toFixed(1);
    s = +(s * 100).toFixed(1);

    l = checkRange(l, 0, 100);
    s = checkRange(s, 0, 100);

    return { h, s, l };
  };

  // eslint-disable-next-line max-statements
  const calculRgb = ({ h, c, x }: { h: number; c: number; x: number }) => {
    let [r, g, b] = [0, 0, 0];

    if (0 <= h && h < 60) {
      r = c;
      g = x;
      b = 0;
    } else if (60 <= h && h < 120) {
      r = x;
      g = c;
      b = 0;
    } else if (120 <= h && h < 180) {
      r = 0;
      g = c;
      b = x;
    } else if (180 <= h && h < 240) {
      r = 0;
      g = x;
      b = c;
    } else if (240 <= h && h < 300) {
      r = x;
      g = 0;
      b = c;
    } else if (300 <= h && h < 360) {
      r = c;
      g = 0;
      b = x;
    }
    return { r, g, b };
  };

  const HSLToRGB = (h: number, s: number, l: number) => {
    s /= 100;
    l /= 100;

    const c = (1 - Math.abs(2 * l - 1)) * s;
    const x = c * (1 - Math.abs(((h / 60) % 2) - 1));
    const m = l - c / 2;
    let { r, g, b } = calculRgb({ c, h, x });

    r = Math.round((r + m) * 255);
    g = Math.round((g + m) * 255);
    b = Math.round((b + m) * 255);

    return checkRGB(r, g, b);
  };

  const getHEX = (code: number) => {
    const hex = code.toString(16);
    return hex.length === 1 ? `0${hex}` : hex;
  };

  const RGBToHEX = (r: number, g: number, b: number) =>
    `#${getHEX(r)}${getHEX(g)}${getHEX(b)}`;

  const lightness = (color: string, percent = 0) => {
    const { hex, check } = checkColor(color);
    if (!check || typeof percent !== "number" || percent === 0) return color;
    percent = checkRange(percent, -100, 100);

    const { r, g, b } = HEXToRGB(hex);
    const hsl = RGBToHSL(r, g, b);
    const { h, s } = hsl;
    let { l } = hsl;
    const range = percent > 0 ? 100 - l : l;
    l += Math.round(range * 0.01 * percent);

    const { r: R, g: G, b: B } = HSLToRGB(h, s, l);

    return RGBToHEX(R, G, B);
  };

  const isWhite = (color: string) => {
    const { hex, check } = checkColor(color);
    if (!check) return false;
    const { r, g, b } = HEXToRGB(hex);
    const y = 0.2126 * r + 0.7152 * g + 0.0722 * b;
    return y > 128 + 64;
  };

  return { lightness, isWhite };
};

import { css, Theme } from "@emotion/react";
import { useColor } from "./hooks/useColor";
import { useCJoli } from "./hooks/useCJoli";

export const zoomIcon = () => css`
  transition: transform 0.1s;
  &:hover {
    transform: scale(1.2);
  }
`;

export const bgSecondary = ({ theme }: { theme: Theme }) =>
  css({
    backgroundColor: `${theme.colors.secondary} !important`,
    color: "white !important",
    textAlign: "center",
  });

export const useGlobal = (theme: Theme) => {
  const { lightness, isWhite } = useColor();
  const {
    theme: { primary },
  } = useCJoli();

  return css`
    body {
      background-color: ${theme.colors.primary};
      color: white;
      uuser-select: none;
    }
    input::placeholder {
      opacity: 0.5 !important;
    }
    span.badge.bg-secondary.menu {
      background-color: grey !important;
      opacity: 0.5;
    }
    .btn-primary {
      --bs-btn-bg: ${lightness(primary, 10)};
      --bs-btn-border-color: ${isWhite(primary)
        ? "black"
        : lightness(primary, 10)};
      --bs-btn-hover-bg: ${lightness(primary, isWhite(primary) ? -5 : 20)};
      --bs-btn-hover-border-color: ${isWhite(primary)
        ? "black"
        : lightness(primary, 20)};
      --bs-btn-active-bg: ${lightness(primary, isWhite(primary) ? -10 : 30)};
      --bs-btn-active-border-color: ${isWhite(primary)
        ? "black"
        : lightness(primary, 30)};
      --bs-btn-disabled-bg: ${lightness(primary, 10)};
      --bs-btn-color: ${isWhite(primary) ? "black" : "white"};
      --bs-btn-hover-color: ${isWhite(primary) ? "black" : "white"};
      --bs-btn-active-color: ${isWhite(primary) ? "black" : "white"};
      --bs-btn-disabled-color: ${isWhite(primary) ? "black" : "white"};
    }
    .progress {
      --bs-progress-bar-bg: ${theme.colors.secondary};
    }
    .accordion-button {
      --bs-accordion-active-bg: ${theme.colors.secondary};
      &:not(collapsed),
      &:not(collapsed)::after {
        --bs-accordion-active-color: white;
        --bs-body-color: white;
      }
    }
    .bg-secondary {
      --bs-bg-opacity: 1;
      --bs-secondary-rgb: 120, 129, 169;
    }
    .form-check-input:checked {
      background-color: ${lightness(primary, isWhite(primary) ? -50 : 10)};
      border-color: ${lightness(primary, isWhite(primary) ? -50 : 10)};
    }

    .nav-pills {
      --bs-nav-pills-link-active-bg: ${theme.colors.primary};
    }

    .popover {
      --bs-popover-max-width: 800px;
    }

    .chat-messages {
      display: flex;
      flex-direction: column;
      max-height: 800px;
      overflow-y: scroll;
    }

    .chat-message-left,
    .chat-message-right {
      display: flex;
      flex-shrink: 0;
    }

    .chat-message-left {
      margin-right: auto;
    }

    .chat-message-right {
      flex-direction: row-reverse;
      margin-left: auto;
    }
  `;
};

import React from "react";
import { ToastContext } from "../contexts/ToastContext";

export const useToast = () => {
  const ctx = React.useContext(ToastContext);
  if (!ctx) {
    throw new Error("useToast has to be used within <ToastProvider>");
  }
  return ctx;
};

import { Star, StarFill } from "react-bootstrap-icons";
import { useCJoli } from "../hooks/useCJoli";
import { zoomIcon } from "../styles";
import styled from "@emotion/styled";
import { useUser } from "../hooks/useUser";
import React from "react";
import { useToast } from "../hooks/useToast";
import { useTranslation } from "react-i18next";
import { useTheme } from "@emotion/react";
import { useParams } from "react-router-dom";
import { useColor } from "../hooks/useColor";
import useScreenSize from "../hooks/useScreenSize";

const MyStar = styled(Star)`
  ${zoomIcon}
`;
const MyStarFill = styled(StarFill)`
  ${zoomIcon}
`;

const MyTeam = styled("span")<{ color: string }>`
  font-weight: 600;
  color: ${(props) => props.color};
`;

const TeamName = ({
  positionId,
  teamId,
  defaultName,
}: {
  positionId?: number;
  teamId?: number;
  defaultName?: string;
}) => {
  const { getTeamInfo, getTeam, findTeam } = useCJoli();
  const { userConfig, isConnected, handleSaveUserConfig } = useUser();
  const { showToast } = useToast();
  const { t } = useTranslation();
  const theme = useTheme();
  const { teamId: currentTeamId } = useParams();
  const { isWhite } = useColor();
  const { isMobile } = useScreenSize();

  const { name, logo } = positionId
    ? getTeamInfo(positionId, defaultName)
    : getTeam(teamId!) || { name: defaultName };

  const team = positionId
    ? findTeam({ positionId })
    : teamId
    ? getTeam(teamId)
    : undefined;

  const fullname =
    team?.datas?.name && !isMobile ? `${name} - ${team.datas.name}` : name;

  const saveFavoriteTeam = React.useCallback(
    async (teamId?: number) => {
      await handleSaveUserConfig({
        ...userConfig,
        favoriteTeamId: teamId || 0,
      });
      teamId
        ? showToast("success", t("user.favoriteSaved", "Favorite team saved"))
        : showToast(
            "success",
            t("user.favoriteRemoved", "Favorite team removed")
          );
    },
    [handleSaveUserConfig, userConfig, showToast, t]
  );

  const isCurrentTeam =
    currentTeamId && team && team.id == parseInt(currentTeamId);

  const color = isWhite(theme.colors.primary) ? "black" : theme.colors.primary;
  return (
    <>
      {isConnected && team && userConfig.favoriteTeamId == team.id && (
        <MyStarFill
          color={theme.colors.secondary}
          role="button"
          onClick={() => saveFavoriteTeam(0)}
        />
      )}
      {isConnected && team && userConfig.favoriteTeamId != team.id && (
        <MyStar
          color="grey"
          role="button"
          onClick={() => saveFavoriteTeam(team.id)}
        />
      )}
      <img
        src={logo}
        style={{ maxWidth: "30px", maxHeight: "30px" }}
        className="mx-2"
      />
      <span>
        {isCurrentTeam ? <MyTeam color={color}>{fullname}</MyTeam> : fullname}
      </span>
      {team?.datas?.logo && (
        <img
          src={team.datas.logo}
          style={{ maxWidth: "30px", maxHeight: "30px" }}
          className="mx-2"
        />
      )}
    </>
  );
};

export default TeamName;

import styled from "@emotion/styled";

const LeftCenterDiv = styled("div")<{ width?: number }>`
  display: inline-flex;
  text-align: left;
  min-width: ${(props) => (props.width ? `${props.width}px` : "200px")};
  align-items: center;
`;

export default LeftCenterDiv;

import { CaretDownFill, CaretUpFill, PauseFill } from "react-bootstrap-icons";
import LeftCenterDiv from "../../../components/LeftCenterDiv";

interface CellValueProps<T> {
  value?: T;
  getLabel?: (a: T) => string | number | undefined;
  val: number | undefined;
  display: boolean;
}
const CellValue = <T,>({
  value,
  val,
  display,
  getLabel,
}: CellValueProps<T>) => {
  if (val == undefined || !display) {
    return "-";
  } else if (value && getLabel) {
    return getLabel(value);
  } else {
    return val;
  }
};

interface CellEvolutionProps {
  result: number;
  valB: number | undefined;
}

const CellEvolution = ({ result, valB }: CellEvolutionProps) => {
  if (result == 1) {
    return <CaretUpFill color="rgb(25, 135, 84)" className="mx-1" />;
  } else if (result == -1) {
    return <CaretDownFill color="rgb(220, 53, 69)" className="mx-1" />;
  } else if (!!valB && result == 0) {
    return <PauseFill style={{ transform: "rotate(90deg)" }} color="#ffc107" />;
  }
};

type TeamCellProps<T> = {
  value?: T;
  valueB?: T;
  call: (a: T) => number | undefined;
  getLabel?: (a: T) => string | number | undefined;
  getInfo?: (a: T) => string;
  up?: boolean;
  active: boolean;
  display: boolean;
};

const TeamCell = <T,>({
  value,
  valueB,
  call,
  getLabel,
  getInfo,
  up,
  active,
  display,
}: TeamCellProps<T>) => {
  const calcul = up
    ? (a: number, b: number) => (a > b ? 1 : a == b ? 0 : -1)
    : (a: number, b: number) => (a < b ? 1 : a == b ? 0 : -1);
  const val = value && call(value);
  const valB = valueB && call(valueB);
  const result =
    val == undefined || !valueB || valB == undefined ? 0 : calcul(val, valB);
  return (
    <td>
      <LeftCenterDiv width={40}>
        <CellValue
          val={val}
          value={value}
          getLabel={getLabel}
          display={display}
        />
        {active && <CellEvolution result={result} valB={valB} />}
        <span className="mx-1" style={{ fontSize: 11, color: "grey" }}>
          {getInfo && value && display && getInfo(value)}
        </span>
      </LeftCenterDiv>
    </td>
  );
};

export default TeamCell;

import { Table } from "react-bootstrap";
import TeamName from "../../../components/TeamName";
import CJoliTooltip from "../../../components/CJoliTooltip";
import React from "react";
import TeamCell from "./TeamCell";
import { Rank, Score, Squad, Team, Tourney } from "../../../models";
import { useCJoli } from "../../../hooks/useCJoli";
import { useTranslation } from "react-i18next";

const percent = (value: number, total: number) =>
  total == 0 ? "" : `${Math.round((value / total) * 100)}%`;

const average = (value: number, total: number) =>
  total == 0 ? "" : `avg: ${(value / total).toFixed(1)}`;

interface TeamTableProps {
  team: Team;
  teamB?: Team;
  squad?: Squad;
}

const useFormatRank = () => {
  const { t } = useTranslation();
  return (rank?: number) => {
    if (rank == undefined) return "";
    if (rank == 0) {
      return ` - ${t("rank.first", "1st")} `;
    } else if (rank == 1) {
      return ` - ${t("rank.second", "2me")} `;
    } else if (rank == 2) {
      return ` - ${t("rank.third", "3rd")} `;
    } else {
      return ` - ${t("rank.rankth", { rank: rank + 1 })}`;
    }
  };
};

const useColumns = (tourney?: Tourney, teamB?: Team) => {
  const { t } = useTranslation();
  const formatRank = useFormatRank();

  const winPt = tourney?.config.win || 2;

  let columns = [
    {
      label: "Rang",
      description: t("rank.ranking", "Ranking"),
      callRank: (r: Rank) => r.order || 0,
      getLabel: (r: Rank) =>
        r.order == 1
          ? `${t("rank.first")} `
          : r.order == 2
            ? `${t("rank.second")} `
            : r.order == 3
              ? `${t("rank.third")} `
              : t("rank.rankth", { rank: r.order }),
      up: false,
      active: true,
      needTeam: true,
    },
    {
      label: "PTS",
      description: t("rank.total", "Points"),
      callScore: (s: Score) => s.total,
      getInfo: (s: Score) => percent(s.total, winPt * s.game),
      up: true,
      active: !!teamB,
      needTeam: true,
    },
    {
      label: "PJ",
      description: t("rank.game", "Games played"),
      callScore: (s: Score) => s.game,
      up: true,
      active: false,
      needTeam: false,
    },
    {
      label: "V",
      description: t("rank.win", "Victories"),
      callScore: (s: Score) => s.win,
      getInfo: (s: Score) =>
        percent(s.win, s.game) + formatRank(s.ranks?.win?.rank),
      up: true,
      active: !!teamB,
      needTeam: false,
    },
    {
      label: "N",
      description: t("rank.neutral", "Drawn games"),
      callScore: (s: Score) => s.neutral,
      getInfo: (s: Score) =>
        percent(s.neutral, s.game) + formatRank(s.ranks?.neutral?.rank),
      up: true,
      active: false,
      needTeam: false,
    },
    {
      label: "D",
      description: t("rank.loss", "Defeats"),
      callScore: (s: Score) => s.loss,
      getInfo: (s: Score) =>
        percent(s.loss, s.game) + formatRank(s.ranks?.loss?.rank),
      up: false,
      active: !!teamB,
      needTeam: true,
    },
    {
      label: "BP",
      description: t("rank.goalFor", "Goals for"),
      callScore: (s: Score) => {
        if (!teamB) return s.goalFor / s.game;
        return s.goalFor;
      },
      getLabel: (s: Score) => s.goalFor,
      getInfo: (s: Score) =>
        average(s.goalFor, s.game) + formatRank(s.ranks?.goalFor?.rank),
      up: true,
      active: true,
      needTeam: false,
    },
    {
      label: "BC",
      description: t("rank.goalAgainst", "Goals against"),
      callScore: (s: Score) => {
        if (!teamB) return s.goalAgainst / s.game;
        return s.goalAgainst;
      },
      getLabel: (s: Score) => s.goalAgainst,
      getInfo: (s: Score) =>
        average(s.goalAgainst, s.game) + formatRank(s.ranks?.goalAgainst?.rank),
      up: false,
      active: true,
      needTeam: false,
    },
    {
      label: "BL",
      description: t("rank.shutOut", "ShutOut"),
      callScore: (s: Score) => {
        if (!teamB) return s.shutOut / s.game;
        return s.shutOut;
      },
      getLabel: (s: Score) => s.shutOut,
      getInfo: (s: Score) =>
        percent(s.shutOut, s.game) + formatRank(s.ranks?.shutOut?.rank),
      up: true,
      active: true,
      needTeam: false,
    },
    {
      label: "+/-",
      description: t("rank.goalDiff", "Goal average"),
      callScore: (s: Score) => {
        if (!teamB) return s.goalDiff / s.game;
        return s.goalDiff;
      },
      getLabel: (s: Score) => s.goalDiff,
      getInfo: (s: Score) =>
        average(s.goalDiff, s.game) + formatRank(s.ranks?.goalDiff?.rank),
      up: true,
      active: true,
      needTeam: false,
    },
  ];
  if (tourney?.config?.hasPenalty) {
    columns = [
      ...columns,
      {
        label: "PEN",
        description: t("rank.penalty", "Penalties"),
        callScore: (s: Score) => s.penalty,
        up: false,
        active: true,
        needTeam: false,
      },
    ];
  }

  return columns;
};

const TeamTable = ({ team, teamB, squad }: TeamTableProps) => {
  const { ranking, tourney, getTeamRank, getScoreForTeam, getScoreFromSquad } =
    useCJoli();
  let rank = getTeamRank(team);
  let rankB = teamB && getTeamRank(teamB);
  const score = getScoreForTeam(team)!;
  let scoreB = teamB && getScoreForTeam(teamB);
  const scoreSquad = squad && getScoreFromSquad(squad, team);
  const scoreSquadB = squad && teamB && getScoreFromSquad(squad, teamB);
  if ((!rank || !rankB) && scoreSquad && scoreSquadB) {
    rank = { order: scoreSquad.rank } as Rank;
    rankB = { order: scoreSquadB.rank } as Rank;
  }

  let datas: { team?: Team; rank?: Rank; score?: Score }[] = [
    { team, rank, score },
  ];
  if (teamB) {
    datas = [...datas, { team: teamB, rank: rankB, score: scoreB }];
  } else {
    const scoreTourney = ranking?.scores.scoreTourney;
    scoreB = scoreTourney;
    datas = [
      ...datas,
      { team: undefined, rank: undefined, score: scoreTourney },
    ];
  }

  const columns = useColumns(tourney, teamB);

  return (
    <Table striped bordered size="sm" style={{ textAlign: "center" }}>
      <thead>
        <tr>
          <th />
          {datas.map(({ team }) => (
            <th key={team?.id || 0}>
              {team ? <TeamName teamId={team.id} /> : tourney?.name}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {columns.map((c, i) => (
          <tr key={i}>
            <td>
              <CJoliTooltip info={c.description}>{c.label}</CJoliTooltip>
            </td>
            {datas.map(({ team, rank, score }, j) => (
              <React.Fragment key={j}>
                {c.callRank ? (
                  <TeamCell
                    value={rank}
                    valueB={rankB}
                    call={c.callRank}
                    getLabel={c.getLabel}
                    getInfo={c.getInfo}
                    active={c.active && j == 0}
                    up={c.up}
                    display={!c.needTeam || !!team}
                  />
                ) : (
                  <TeamCell
                    value={score}
                    valueB={scoreB}
                    call={c.callScore!}
                    getLabel={c.getLabel}
                    getInfo={c.getInfo}
                    active={c.active && j == 0}
                    up={c.up}
                    display={!c.needTeam || !!team}
                  />
                )}
              </React.Fragment>
            ))}
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default TeamTable;

import styled from "@emotion/styled";
import { Overlay, Popover, Button } from "react-bootstrap";
import { ArrowsCollapseVertical } from "react-bootstrap-icons";
import React from "react";
import TeamTable from "../team/TeamTable";
import { Squad, Team } from "../../../models";
import { Trans } from "react-i18next";

const MyButton = styled(ArrowsCollapseVertical)`
  color: #bbb;
  &:hover {
    color: black;
  }
`;

interface CompareButtonProps {
  team: Team;
  teamB: Team;
  squad?: Squad;
}

const CompareButton = ({ team, teamB, squad }: CompareButtonProps) => {
  const [open, setOpen] = React.useState(false);
  const target = React.useRef(null);
  return (
    <>
      <div ref={target} className="d-inline">
        <MyButton
          role="button"
          size={20}
          className="mx-2"
          onClick={() => setOpen(!open)}
        />
      </div>
      <Overlay
        target={target.current}
        show={open}
        rootClose
        onHide={() => setOpen(false)}
        placement="auto"
      >
        {(props) => (
          <Popover {...props}>
            <Popover.Header style={{ color: "black" }}>
              <Trans i18nKey="match.compare">Compare</Trans>
            </Popover.Header>
            <Popover.Body>
              <div>
                <TeamTable team={team} teamB={teamB} squad={squad} />
              </div>
              <Button onClick={() => setOpen(false)} size="sm">
                <Trans i18nKey="button.close">Close</Trans>
              </Button>
            </Popover.Body>
          </Popover>
        )}
      </Overlay>
    </>
  );
};

export default CompareButton;

import { Dropdown, DropdownButton, Form, InputGroup } from "react-bootstrap";
import { Match } from "../../../models";
import { useUser } from "../../../hooks/useUser";
import { FieldValues, UseFormRegister } from "react-hook-form";
import { Trans } from "react-i18next";
import { useCJoli } from "../../../hooks/useCJoli";

interface ScoreCellInputProps {
  id: string;
  match: Match;
  teamA?: boolean;
  teamB?: boolean;
  saveMatch: (match: Match) => void;
  register: UseFormRegister<FieldValues>;
}

const ScoreCellInput = ({
  id,
  match,
  teamA,
  saveMatch,
  register,
}: ScoreCellInputProps) => {
  const { isConnected } = useUser();
  const { tourney } = useCJoli();
  const placeholder = teamA
    ? match.estimate?.scoreA.toString()
    : match.estimate?.scoreB.toString();
  return (
    <InputGroup size="sm" style={{ width: "80px" }}>
      <Form.Control
        type="number"
        min="0"
        max="100"
        {...register(id)}
        placeholder={placeholder}
        data-testid={id}
      />
      {isConnected && tourney?.config?.hasForfeit && (
        <DropdownButton variant="outline-secondary" title="">
          <Dropdown.Item
            href="#"
            onClick={() =>
              saveMatch({
                ...match,
                forfeitA: !!teamA,
                forfeitB: !teamA,
                scoreA: 0,
                scoreB: 0,
              })
            }
            data-testid={`${id}.forfeit`}
          >
            <Trans i18nKey="match.forfeit">Forfeit</Trans>
          </Dropdown.Item>
        </DropdownButton>
      )}
    </InputGroup>
  );
};

export default ScoreCellInput;

import React from "react";
import { Spinner } from "react-bootstrap";
import { CheckCircle, XCircle } from "react-bootstrap-icons";

interface ScoreButtonProps {
  id: string;
  action: "remove" | "save";
  onClick: () => Promise<void>;
}

const ScoreButton = ({ id, action, onClick }: ScoreButtonProps) => {
  const [loading, setLoading] = React.useState(false);
  const handleClick = async () => {
    setLoading(true);
    await onClick();
    setLoading(false);
  };
  return (
    <>
      {action == "save" && !loading && (
        <CheckCircle
          color="rgb(25, 135, 84)"
          size={32}
          role="button"
          onClick={handleClick}
          data-testid={id}
        />
      )}
      {action == "remove" && !loading && (
        <XCircle
          color="rgb(220, 53, 69)"
          size={32}
          role="button"
          onClick={handleClick}
          data-testid={id}
        />
      )}
      {loading && (
        <Spinner
          animation="grow"
          variant={action == "remove" ? "danger" : "success"}
        />
      )}
    </>
  );
};

export default ScoreButton;

import { Badge } from "react-bootstrap";
import { IMatch } from "../../../models";

interface ScoreCellViewProps {
  match: IMatch;
  mode: "A" | "B";
}

const ScoreCellView = ({ match, mode }: ScoreCellViewProps) => {
  let badge =
    (mode == "A" && match.scoreA > match.scoreB) ||
    (mode == "B" && match.scoreA < match.scoreB)
      ? "success"
      : match.scoreA === match.scoreB
      ? "warning"
      : "danger";
  if (match.forfeitA) {
    badge = mode == "A" ? "danger" : "success";
  } else if (match.forfeitB) {
    badge = mode == "A" ? "success" : "danger";
  }
  const text = badge == "warning" ? "black" : "white";

  return (
    <Badge bg={badge} text={text} style={{ fontSize: "18px" }} className="my-1">
      {mode == "A" ? match.scoreA : match.scoreB}
    </Badge>
  );
};

export default ScoreCellView;

import React, { ReactNode } from "react";
import { Button, Spinner } from "react-bootstrap";

interface ButtonLoadingProps {
  variant?: "danger";
  children: ReactNode;
  onClick: () => Promise<void>;
}

const ButtonLoading = ({ variant, children, onClick }: ButtonLoadingProps) => {
  const [loading, setLoading] = React.useState(false);
  const handleClick = async () => {
    setLoading(true);
    await onClick();
    setLoading(false);
  };
  return (
    <span className="d-inline-flex mx-2">
      {!loading && (
        <Button variant={variant} onClick={handleClick}>
          {children}
        </Button>
      )}
      {loading && <Spinner animation="grow" variant={variant} />}
    </span>
  );
};

export default ButtonLoading;

import React from "react";
import {
  OverlayTrigger,
  Tooltip,
  Badge,
  Popover,
  Button,
  Overlay,
} from "react-bootstrap";
import { Alt, Trash } from "react-bootstrap-icons";
import ButtonLoading from "./ButtonLoading";
import { Trans, useTranslation } from "react-i18next";

interface SimulationIconProps {
  show: boolean;
  title?: string;
  onRemove?: () => Promise<void>;
}

const SimulationIcon = ({ show, title, onRemove }: SimulationIconProps) => {
  const [open, setOpen] = React.useState(false);
  const { t } = useTranslation();
  const target = React.useRef(null);
  const handleRemove = async () => {
    if (onRemove) {
      await onRemove();
      setOpen(false);
    }
  };
  return (
    <>
      {show && !onRemove && (
        <OverlayTrigger overlay={<Tooltip>Simulation</Tooltip>}>
          <Badge className="mx-2" bg="secondary">
            <Alt />
          </Badge>
        </OverlayTrigger>
      )}
      {show && onRemove && (
        <>
          <Badge
            className="mx-2"
            bg="secondary"
            role="button"
            ref={target}
            onClick={() => setOpen(!open)}
          >
            <Alt />
          </Badge>
          <Overlay
            target={target.current}
            show={open}
            rootClose
            onHide={() => setOpen(false)}
          >
            {(props) => (
              <Popover {...props}>
                <Popover.Header style={{ color: "black" }}>
                  {title ?? t("simulation.tooltip", "Simulation")}
                </Popover.Header>
                <Popover.Body>
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      height: 30,
                    }}
                    className="mb-3"
                  >
                    <Trans i18nKey="simulation.removeAll">
                      Remove all simulations
                    </Trans>
                    <ButtonLoading variant="danger" onClick={handleRemove}>
                      <Trash />
                    </ButtonLoading>
                  </div>
                  <Button onClick={() => setOpen(false)} size="sm">
                    <Trans i18nKey="button.close">Close</Trans>
                  </Button>
                </Popover.Body>
              </Popover>
            )}
          </Overlay>
        </>
      )}
    </>
  );
};

